import React, { useState, useContext } from "react";
import { ThemeContext } from "../../context/Theme";

export const AdminAccount = ({ setSelected }) => {
  const [theme, setTheme] = useContext(ThemeContext);
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const img = URL.createObjectURL(e.target.files[0]);
      setSelectedImage(img);
    }
  };

  const handleAddAccount = () => {
    // Logic to add account
    setSelected(null); // Close modal after adding
  };

  return (
    <div className="flex flex-col">
      <h2 className="text-2xl font-bold mb-4">Add Admin Account</h2>
      <div className="mb-4">
        {selectedImage ? (
          <img src={selectedImage} alt="Selected" className="w-full h-auto rounded-lg mb-4" />
        ) : (
          <div className="border-2 border-gray-300 h-40 flex items-center justify-center mb-4 rounded-lg">
            <span className="text-gray-500">No Image Selected</span>
          </div>
        )}
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          className="hidden"
          id="select-image"
        />
        <label
          htmlFor="select-image"
          className="inline-block bg-green-500 text-white px-4 py-2 rounded-lg cursor-pointer hover:bg-green-600"
        >
          Select Image
        </label>
      </div>
      <input type="text" placeholder="Username" className="w-full mb-4 p-2 rounded-lg border border-gray-300" />
      <input type="email" placeholder="Email" className="w-full mb-4 p-2 rounded-lg border border-gray-300" />
      <input type="password" placeholder="Password" className="w-full mb-4 p-2 rounded-lg border border-gray-300" />
      <input type="password" placeholder="Confirm Password" className="w-full mb-4 p-2 rounded-lg border border-gray-300" />
      <div className="flex justify-between">
        <button onClick={handleAddAccount} className="rounded-lg bg-green-500 text-white px-4 py-2 hover:bg-green-600">Add Account</button>
      </div>
    </div>
  );
};
