import React from "react";

function DrawerButton({
  theme,
  selected,
  sideBarOpen,
  title,
  handleSelectedButton,
}) {
  return (
    <button
      tabIndex="0"
      className={`flex  items-center w-full p-3 rounded-lg text-center  leading-tight transition-all btn ${
        selected === title && theme === "night"
          ? "btn-outline border-gray-400 bg-base-300 text-white "
          : "border-none bg-base-100"
      }} ${
        selected === title && theme === "fantasy"
          ? "btn-outline border-gray-400 bg-gray-1  text-white bg-night"
          : "border-none  bg-base-100"
      }`}
    >
      <div className="grid gap-3 grid-flow-col justify-center text-md items-start lg:text-lg">
        <i class="fa-solid fa-tv text-start pt-1 "></i>

        {sideBarOpen ? title : ""}
      </div>
    </button>
  );
}

export default DrawerButton;
