import NotificationTable from "../../components/ui/NotificationTable";
import React, { useState, useContext, useEffect } from "react";
import { ThemeContext } from "../../context/Theme";
import { UserContext } from "../../context/User";
import { SuccessContext } from "../../context/Success";
import { AlertsContext } from "../../context/Alerts";
import axios from "axios";

{
  /*Get Props import axios from "axios";*/
}
export const Notification = ({ setSelected }) => {
  const [theme, setTheme] = useContext(ThemeContext);
  const [titles, setTitle] = useContext(null);
  const [message, setMessage] = useState(null);
  const [type, setType] = useState(null);
  const [user, setUser] = useContext(UserContext);
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const authToken = localStorage.getItem("authToken");
  const refreshToken = localStorage.getItem("refreshToken");
  const serverUrl = process.env.REACT_APP_SERVER_URL;

  useEffect(() => {
    const fetchNotification = async () => {
      try {
        setErrors([]);
        setSuccess(false);

        const response = await axios.get(
          `${serverUrl}/notification/view`,

          {
            headers: {
              "x-auth-token": authToken,
              "x-refresh-token": refreshToken,
            },
          },
          {}
        );
        setTitle(response.data.title);
        setUser(response.data.account);
        setSuccess(true); // Set success only if the request succeeds
      } catch (error) {
        console.error(error);
        setSuccess(false);
        if (error.response && error.response.data.errors) {
          setErrors(error.response.data.errors.map((err) => err.msg));
        } else {
          setErrors(["An unexpected error occurred."]); // Generic fallback
        }
      }
    };

    fetchNotification();

    // Optionally return a cleanup function
    return () => {
      setTitle([]); // Example cleanup, adjust as needed
    };
  }, []); // Dependencies to rerun effect only when these values change

  return (
    <div
      className={`col-span-8 overflow-hidden rounded-lg text-xs md:text-md w-64 px-8 sm:w-full `}
    >
      <div className="flex justify-end px-9 py-3">
        {user.role != "P" && (
          <button
            id="btn_AddNotification"
            onClick={() => {
              setSelected("AddNotification");
            }}
            className="rounded-full bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-6"
          >
            Add Notification
          </button>
        )}
      </div>
      {titles &&
        titles.map((title) => (
          <NotificationTable
            key={title.ntf_id}
            setSelected={setSelected}
            user={user}
            title={title}
            message={message}
            type={type}
          />
        ))}
    </div>
  );
};
