import React, { useState, useContext } from "react";
import { ThemeContext } from "../../context/Theme";

const DataTable = ({ accvalues, setSelected }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 5;
  const [theme] = useContext(ThemeContext);

  const textColor = theme === "night" ? "text-white" : "text-black";
  const bgColor = theme === "night" ? "bg-gray-800" : "bg-white";

  // Filter data based on search term
  const filteredData = accvalues.filter(({ username, email, date, status }) =>
    [username, email, date, status].some((field) =>
      field.toLowerCase().includes(searchTerm.toLowerCase())
    )
  );

  // Reset currentPage if search results change
  const totalPages = Math.ceil(filteredData.length / rowsPerPage);
  if (currentPage > totalPages) setCurrentPage(1);

  const paginatedData = filteredData.slice(
    (currentPage - 1) * rowsPerPage,
    currentPage * rowsPerPage
  );

  return (
    <div className={`p-6 w-full ${bgColor} ${textColor}`}>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className={`w-full mb-4 p-2 rounded-lg border ${theme === "night" ? "border-gray-600 bg-gray-800" : "border-gray-300"}`}
      />

      <table className="table-auto w-full">
        <thead>
          <tr>
            <th>Action</th>
            <th>Username</th>
            <th>Email</th>
            <th>Date Created</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {paginatedData.length ? (
            paginatedData.map((item) => (
              <tr key={item.id}>
                <td>
                  <button onClick={() => setSelected("Receipt")}>
                    <i className="fa-solid fa-eye"></i>
                  </button>
                </td>
                <td>{item.username}</td>
                <td>{item.email}</td>
                <td>{item.date}</td>
                <td>{item.status}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">No results found.</td>
            </tr>
          )}
        </tbody>
      </table>

      <div className="flex justify-end mt-4">
        <button
          disabled={currentPage === 1}
          onClick={() => setCurrentPage(currentPage - 1)}
        >
          &lt;
        </button>
        <span>{currentPage}</span>
        <button
          disabled={currentPage === totalPages}
          onClick={() => setCurrentPage(currentPage + 1)}
        >
          &gt;
        </button>
      </div>
    </div>
  );
};

export default DataTable;
