import { React, useState, useEffect } from "react";
import NavBar from "../components/NavBar";
import logo from "../assets/logo1.png";

function Main({ theme, toggleTheme }) {
  const [selected, setSelected] = useState("");
  const [sideBarOpen, setSideBarOpen] = useState(true);
  const toggleSideBar = () => {
    setSideBarOpen(!sideBarOpen);
  };

  useEffect(() => {
    const screenWidth = window.innerWidth;
    if (screenWidth <= 768) setSideBarOpen(false);
  }, []);

  return (
    <>
      <div className="flex flex-row w-screen ">
        <div
          className={`relative flex-1 h-svh flex-col bg-base-100 border-r-2 border-gray-200 text-fantasy  w-full max-w-[20rem] p-4 shadow-xl shadow-blue-gray-900/5 ${
            sideBarOpen ? "flex min-w-[240px]" : "w-20"
          }`}
        >
          <div className="mb-2 px-2 py-4">
            <h5
              className={`flex gap-2 antialiased tracking-normal font-sans text-lg font-semibold leading-snug ${
                theme === "night" ? "text-fantasy " : "text-night "
              }`}
            >
              <img className="h-7 w-7 btn-square bg-contain  " src={logo} />
              {sideBarOpen ? "Pugad Maharlika" : ""}
            </h5>
          </div>
          <nav className="flex flex-col gap-1 p-0 font-sans text-base font-normal">
            <button
              tabIndex="0"
              className={`flex  items-center w-full p-3 rounded-lg text-center  leading-tight transition-all btn ${
                selected === "dashboard" && theme === "night"
                  ? "btn-outline border-gray-400 bg-gray-1 text-white "
                  : "border-none  bg-base-300 "
              }} ${
                selected === "dashboard" && theme === "fantasy"
                  ? "btn-outline border-gray-400 bg-gray-1  text-white bg-night"
                  : "border-none  bg-base-200 text-white"
              }`}
            >
              <div className="grid gap-3 grid-flow-col justify-center text-md items-start lg:text-lg">
                <i class="fa-solid fa-tv text-start pt-1 "></i>

                {sideBarOpen ? "Dashboard" : ""}
              </div>
            </button>
          </nav>
        </div>
        <div className="flex flex-col h-svh w-svw">
          <NavBar
            theme={theme}
            toggleTheme={toggleTheme}
            toggleSideBar={toggleSideBar}
          />
          <div className="relative flex-2"></div>
        </div>
      </div>
    </>
  );
}

export default Main;
