import React from "react";
import { ThemeContext } from "../../context/Theme";
import { useContext, useState } from "react";
import { AlertsContext } from "../../context/Alerts";
import { SuccessContext } from "../../context/Success";
import { UserContext } from "../../context/User";
import UploadImage from "../../service/UploadImage";
import axios from "axios";
import API from "../../service/API";

export const AddOffer = ({ setSelected }) => {
  const [image, setImage] = useState(null);
  const [theme] = useContext(ThemeContext);
  const [name, setName] = useState("");
  const [value, setValue] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");

  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const [user, setUser] = useContext(UserContext);
  const authToken = localStorage.getItem("authToken");
  const refreshToken = localStorage.getItem("refreshToken");
  const serverUrl = process.env.REACT_APP_SERVER_URL;

  // Image Uploader from local
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setImage(imageUrl);
    }
  };

  // Handle Offer creation
  const handleCreateOffer = async () => {
    setErrors([]);
    setSuccess(false);
    setSelected("Offer");
    //Validator
    if (!name || !value || !price || !description) {
      setErrors(["All fields are required..."]);
      return;
    }

    // SEND API REQUEST
    const config = {
      url: `${serverUrl}/offer/addoffer`,
      method: "POST",
      data: {
        url: url,
        name: name,
        value: value,
        price: price,
        description: description,
      },
    };

    const { res, error, loading } = await API(config);
    if (res) {
      setSuccess(true);
      setErrors(["Offer added successfuly!"]);
    }
    if (error) console.log(error);
  };

  const handleOfferimg = async () => {
    await UploadImage(iamge, setSuccess, setErrors, handleCreateOffer, user);
  };
  return (
    <>
      <div className="col-span-8 overflow-hidden rounded-lg text-xs md:text-md w-64 px-8 sm:w-full h-full">
        <div className="flex justify-between items-center py-4">
          <h1 className="text-3xl font-bold">Add Offer</h1>
          <button
            id="btn_back"
            onClick={() => {
              setSelected("Offer");
            }}
            className=" hover:text-blue-700 font-bold px-4 rounded"
          >
            <i className="fa-solid fa-circle-chevron-left text-3xl"></i>
          </button>
        </div>

        <div className="flex bg-white items-center w-full p-8 rounded-lg shadow-lg">
          {/*For Image uploading*/}
          <div className="flex flex-col items-center mb-5 w-1/3">
            <div className="relative">
              <img
                src={
                  image ||
                  "https://img.freepik.com/premium-vector/no-photo-available-vector-icon-default-image-symbol-picture-coming-soon-web-site-mobile-app_87543-10639.jpg?w=740"
                }
                alt="Uploaded"
                className="mt-4 w-40 h-40 object-cover rounded-lg"
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  setImage(e.target.files[0]);
                  handleImageUpload(e);
                }}
                className="absolute top-0 left-0 w-full h-full opacity-0 cursor-pointer"
              />
            </div>
            <p className="mt-2 text-gray-600">Click to upload a new image</p>
          </div>

          <div className="w-2/3">
            <input
              type="text"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.targer.value)}
              className="w-full p-2 rounded-lg mb-4 border border-gray-300 focus:border-green-500"
            />
            <input
              type="number"
              step="1"
              max="9999999"
              placeholder="Value"
              value={value}
              onChange={(e) => setValue(e.targer.value)}
              className="w-full p-2 rounded-lg mb-4 border border-gray-300 focus:border-green-500"
            />
            <input
              type="number"
              step="1"
              max="9999999"
              placeholder="Price"
              value={price}
              onChange={(e) => setPrice(e.targer.value)}
              className="w-full p-2 rounded-lg mb-4 border border-gray-300 focus:border-green-500"
            />
            <textarea
              placeholder="Details"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full p-2 rounded-lg mb-4 border border-gray-300 focus:border-green-500"
            ></textarea>

            <div className="flex w-full justify-end">
              <button
                onClick={() => {
                  setSelected("Offer");
                  handleCreateOffer();
                }}
                className="bg-green-500 hover:bg-green-600 text-white font-bold px-6 py-3 rounded-lg shadow-md hover:shadow-lg transition duration-300"
              >
                Add
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
