import React, { useEffect, useContext, useState } from "react";
import axios from "axios";
import { ThemeContext } from "../../context/Theme";
import { AlertsContext, SuccessContext } from "../../context/Alerts";
import { TransactionTable } from "../../components/ui/Table";

export const Transactions = ({ setSelected }) => {
  const [theme] = useContext(ThemeContext);
  const [transactions, setTransactions] = useState(null);
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const authToken = localStorage.getItem("authToken");
  const refreshToken = localStorage.getItem("refreshToken");
  const serverUrl = process.env.REACT_APP_SERVER_URL;

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        setErrors([]);
        setSuccess(false);

        const response = await axios.get(`${serverUrl}/transactions/view`, {
          headers: {
            "x-auth-token": authToken,
            "x-refresh-token": refreshToken,
          },
        });
        setTransactions(response.data.transactions);
        setSuccess(true);
      } catch (error) {
        console.error(error);
        setSuccess(false);
        if (error.response && error.response.data.errors) {
          setErrors(error.response.data.errors.map((err) => err.msg));
        } else {
          setErrors(["An unexpected error occurred."]);
        }
      }
    };

    fetchTransactions();

    return () => {
      setTransactions([]);
    };
  }, []);

  return (
    <div className="container mx-auto p-4">
      <div
        className={`flex w-full rounded-xl h-16 shadow-md bg-fantasy p-4 pl-4 py-4 font-bold items-center mb-4 ${
          theme === "night" ? "bg-night text-white" : "bg-fantasy text-black"
        }`}
      >
        <input
          type="text"
          placeholder="Search..."
          className="border border-gray-300 p-2 rounded-l-lg mr-2"
        />
        <select className="border border-gray-300 p-2 rounded-r-lg">
          <option disabled selected>
            Choose Type
          </option>
          <option>Option 2</option>
          <option>Option 3</option>
        </select>
      </div>

      {errors && errors.length > 0 && (
        <div className="text-red-500 mb-4">
          {errors.map((error, index) => (
            <p key={index}>{error}</p>
          ))}
        </div>
      )}

      {success && transactions ? (
        <TransactionTable
          transactions={transactions}
          setSelected={setSelected}
        />
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
};

export default Transactions;
