import { React, useState, useEffect } from "react";
import { Router, Route, BrowserRouter, Routes } from "react-router-dom";
import Landing from "./pages/Landing";
import ResetPassword from "./pages/ResetPassword";
import Dashboard from "./pages/user/Dashboard";
import Alert from "./components/ui/Alert";
import Main from "./pages/Main";

function App() {
  const savedTheme = localStorage.getItem("theme");
  const [cookie, setCookie] = useState(localStorage.getItem("cookie"));
  const [theme, setTheme] = useState(savedTheme ? savedTheme : "fantasy");

  const handleCoookie = () => {
    setCookie("allowed");
    localStorage.setItem("cookie", "allowed");
  };
  const toggleTheme = () => {
    setTheme(theme === "night" ? "fantasy" : "night");
  };

  useEffect(() => {
    document.querySelector("html").setAttribute("data-theme", theme);
    if (cookie === "allowed") localStorage.setItem("theme", theme);
  }, [theme]);

  return (
    <BrowserRouter>
      {/* Alert container */}
      <div className="fixed z-40 bottom-5 right-2 md:right-3 lg:right-5">
        <div className="flex flex-col gap-3 justify-items-end justify-end items-end p-5">
          {errors.length > 0
