              ))
            : ""}
        </div>
      </div>
      <Routes>
        <Route
          index
          element={
            <Landing
              cookie={cookie}
              theme={theme}
              toggleTheme={toggleTheme}
              handleCoookie={handleCoookie}
            />
          }
        />
        <Route path="/dashboard" element={<Main theme={theme} toggleTheme={toggleTheme} />} />
        <Route path="/reset" element={<ResetPassword theme={theme} toggleTheme={toggleTheme} />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
