import React, { useContext } from "react";
import LineChart from "../../components/ui/LineChart";
import DataTable from "../../components/ui/DataTable";
import { ThemeContext } from "../../context/Theme";

const lineChartData = {
  labels: [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ],
  datasets: [
    {
      label: "Player Activity",
      data: [120, 150, 180, 210, 230, 220, 250, 300, 270, 320, 310, 330],
      backgroundColor: "rgba(54, 162, 235, 0.4)",
      borderColor: "rgba(54, 162, 235, 1)",
      borderWidth: 2,
      pointBackgroundColor: "rgba(54, 162, 235, 1)",
    },
  ],
};

// Player data with toggle status
const playerData = [
  {
    id: 1,
    username: "PlayerOne",
    email: "playerone@gmail.com",
    date: "28 Jan",
    status: true,
  },
  {
    id: 2,
    username: "WarriorX",
    email: "warriorx@gmail.com",
    date: "25 Jan",
    status: false,
  },
  {
    id: 3,
    username: "MageMaster",
    email: "magician@gmail.com",
    date: "20 Jan",
    status: false,
  },
  {
    id: 4,
    username: "RogueRider",
    email: "rogue@gmail.com",
    date: "15 Jan",
    status: true,
  },
  {
    id: 5,
    username: "ArcherQueen",
    email: "archer@gmail.com",
    date: "14 Jan",
    status: true,
  },
];

const PlayerManagement = ({ setSelected }) => {
  const [theme] = useContext(ThemeContext);

  // Toggle activation status
  const handleToggleStatus = (id) => {
    const player = playerData.find((p) => p.id === id);
    if (player) {
      player.status = !player.status;
      alert(
        `${player.username} is now ${player.status ? "Active" : "Disabled"}`
      );
    }
  };

  return (
    <div
      className={`col-span-8 rounded-lg text-xs md:text-md w-64 px-8 sm:w-full`}
    >
      {/* Header Section */}
      <div
        className={`flex justify-between items-center w-full rounded-xl shadow-md py-3 font-bold ${
          theme === "night" ? "bg-night text-white" : "bg-fantasy text-black"
        } mb-4`}
      >
        <h1 className="text-2xl font-semibold">Player Management</h1>
        <button
          onClick={() => setSelected("AddPlayerAccount")}
          className="hover:bg-blue-700 bg-blue-500 text-white rounded-lg px-4 py-2"
        >
          + Add Player
        </button>
      </div>

      {/* Data Table */}
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white rounded-lg">
          <thead>
            <tr>
              <th className="py-2 px-4 border-b">Username</th>
              <th className="py-2 px-4 border-b">Email</th>
              <th className="py-2 px-4 border-b">Joined Date</th>
              <th className="py-2 px-4 border-b">Status</th>
              <th className="py-2 px-4 border-b">Action</th>
            </tr>
          </thead>
          <tbody>
            {playerData.map((player) => (
              <tr key={player.id} className="text-center">
                <td className="py-2 px-4 border-b">{player.username}</td>
                <td className="py-2 px-4 border-b">{player.email}</td>
                <td className="py-2 px-4 border-b">{player.date}</td>
                <td className="py-2 px-4 border-b">
                  {player.status ? "Active" : "Disabled"}
                </td>
                <td className="py-2 px-4 border-b">
                  <button
                    onClick={() => handleToggleStatus(player.id)}
                    className={`px-4 py-1 rounded-lg ${
                      player.status ? "bg-green-500" : "bg-red-500"
                    } text-white`}
                  >
                    {player.status ? "Deactivate" : "Activate"}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Line Chart */}
      <div className="pt-4 mt-4">
        <div
          className={`place-content-center rounded-xl p-5 shadow-md flex flex-col w-full max-w-2xl ${
            theme === "night" ? "bg-night" : "bg-fantasy"
          }`}
        >
          <LineChart title_text={"Player Activity"} data={lineChartData} />
        </div>
      </div>
    </div>
  );
};

export default PlayerManagement;
