import React, { useState, useContext, useEffect } from "react";
import { ThemeContext } from "../../context/Theme";
import { UserContext } from "../../context/User";
import { SuccessContext } from "../../context/Success";
import { AlertsContext } from "../../context/Alerts";
import axios from "axios";
import API from "../../service/API";
import perlas from "../../../src/assets/Perlas.png";

export const ViewOffer = ({ setSelected, setOfferselected }) => {
  const [theme, setTheme] = useContext(ThemeContext);
  const [offers, setOffers] = useState(null);
  const [user, setUser] = useContext(UserContext);
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const authToken = localStorage.getItem("authToken");
  const refreshToken = localStorage.getItem("refreshToken");
  const serverUrl = process.env.REACT_APP_SERVER_URL;

  useEffect(() => {
    const handleViewOffer = async () => {
      setErrors([]);
      setSuccess(false);
      const config = {
        url: `${serverUrl}/offer/viewOffer?ofr_id=${setOfferselected}`,
        method: "GET",
        data: {},
      };

      const { res, error, loading } = await API(config);
      if (res) {
        setIsloading(false);
        setUser(res.data.account);
        setOffers(res.data.offers);
        console.log(res);
      }
      if (error) console.log(error);
    };
    handleViewOffer();
  }, []);

  const handleEditOffer = (ofr_id) => {
    setSelected("");
  };
  return (
    <div
      className={`col-span-8 overflow-hidden rounded-lg shadow-lg  text-xs md:text-md w-64 px-8 sm:w-full py-10 ${
        theme === "night" ? "bg-night text-white " : "bg-fantasy text-black"
      }`}
    >
      <div className="flex justify-between w-full px-4">
        <button
          onClick={() => {
            setSelected("Offer");
          }}
          className=" hover:text-blue-700  font-bold py-2 px-4 rounded "
        >
          <i class="fa-solid fa-circle-chevron-left  text-3xl"></i>
        </button>
      </div>
      <div className="flex  text-balance mt-5">
        <img src=" " alt="Item" className="w-1/2" />
        <div className="ml-5">
          <h2 className="text-2xl font-bold">Item Title</h2>
          <p className=" text-xs mt-2">Value: 3000</p>
          <p className=" mt-2">Price: 2000</p>
          <p className="mt-2">
            Description: Lorem ipsum dolor sit amet, consectetur adipiscing
            elit.
          </p>
          <p className=" mt-2">Status: Released</p>
          <p className="mt-2">Date Created: 01/01/2024</p>
          <p className="mt-2">Date Updated: 01/02/2024</p>
        </div>
      </div>
      <div className="flex w-full justify-end">
        {user.role != "P" && (
          <button
            id="btn_UpdateOffer"
            onClick={() => {
              setSelected("UpdateOffer");
            }}
            className="hover:bg-green-700   bg-green-500 text-white rounded-lg px-4 py-2 mt-4 "
          >
            <i class="fa-solid fa-pen mr-2"></i>
            Edit
          </button>
        )}
      </div>
    </div>
  );
};
