import React from 'react';
import ItemCard from '../../components/ui/ItemCard';
import { ThemeContext } from "../../context/Theme";
import { useContext, useState } from 'react';

export const AdminAddItem = ({setSelected}) => {
    const [theme] = useContext(ThemeContext);
    const [image, setImage] = useState(null);
    const [inputs, setInputs] = useState({ name: '', email: '', phone: '', message: '' });

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        const reader = new FileReader();
    
        reader.onloadend = () => {
          setImage(reader.result);
        };
    
        if (file) {
          reader.readAsDataURL(file);
        }
      };
    
      const handleInputChange = (e) => {
        setInputs({
          ...inputs,
          [e.target.name]: e.target.value
        });
      };
    
      const handleSubmit = (e) => {
        e.preventDefault();
        // Add your submit logic here
      };

    return (
        <>
        <div className="col-span-8 overflow-hidden rounded-lg text-xs md:text-md w-64 px-8 sm:w-full h-full">
    <div className="flex justify-between items-center py-4">
        <h1 className="text-3xl font-bold">Items</h1>
        <button onClick={ () => {setSelected("Items")}} className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">
            Back Button
        </button>
    </div>
</div>

<div className="flex justify-center items-center h-screen bg-gray-200">
  <div className="flex flex-col space-y-8 items-center">
    <div>
      <input type="file" onChange={handleImageUpload} />
      {image && <img src={image} alt="Uploaded" className="mt-4 rounded-lg" style={{ width: '200px' }} />}
    </div>
    <div>
      <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
        <input
          type="text"
          name="name"
          value={inputs.name}
          onChange={handleInputChange}
          placeholder="Name"
          className="border border-gray-400 p-2 rounded-lg"
        />
        <input
          type="email"
          name="email"
          value={inputs.email}
          onChange={handleInputChange}
          placeholder="Email"
          className="border border-gray-400 p-2 rounded-lg"
        />
        <input
          type="tel"
          name="phone"
          value={inputs.phone}
          onChange={handleInputChange}
          placeholder="Phone"
          className="border border-gray-400 p-2 rounded-lg"
        />
        <textarea
          name="message"
          value={inputs.message}
          onChange={handleInputChange}
          placeholder="Message"
          className="border border-gray-400 p-2 rounded-lg"
        ></textarea>
        <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded-lg">Submit</button>
      </form>
    </div>
  </div>
</div>


</>
);
};

export default AdminAddItem;
