import { React, useEffect, useState } from "react";
import oneal from "../assets/oneal_profile.png";

function NavBar({ theme, toggleTheme, toggleSideBar }) {
  const [sideBarOpen, setSideBarOpen] = useState(true);
  const [scrolled, setScrolled] = useState(false);
  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      if (offset > 0) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="h-auto  w-full flex-2 flex flex-row z-50 py-3 bg-base-100 border-b-2  border-gray-200">
      <div className="flex-1 navbar-start">
        <button
          onClick={() => {
            toggleSideBar();
            setSideBarOpen(!sideBarOpen);
          }}
          className="ml-2 btn bg-transparent border-none  "
        >
          {sideBarOpen ? (
            <i class="fa-solid fa-angle-left"></i>
          ) : (
            <i class="fa-solid fa-angle-right"></i>
          )}
        </button>
      </div>
      <div className="navbar-center flex-1 hidden lg:flex"></div>
      <div className="flex-1 flex gap-3 justify-end mr-2">
        <img
          className="rounded-full btn btn-circle border-inherit"
          src={oneal}
          alt="mockup"
        />
        <details className="fixed mt-20" open>
          <summary>Parent item</summary>
          <ul>
            <li>
              <a>Submenu 1</a>
            </li>
            <li>
              <a>Submenu 2</a>
            </li>
            <li>
              <details open>
                <summary>Parent</summary>
                <ul>
                  <li>
                    <a>item 1</a>
                  </li>
                  <li>
                    <a>item 2</a>
                  </li>
                </ul>
              </details>
            </li>
          </ul>
        </details>
      </div>
    </div>
  );
}

export default NavBar;
