import React, { useContext } from "react";
import { ThemeContext } from "../../context/Theme";
import logo from "../../../src/assets/logo1.png"; // Consider using dynamic images instead of a static logo
// Removed unused imports

const ItemCard = ({ setSelected, user, name, description, image }) => {
  const [theme] = useContext(ThemeContext); // Removed setTheme as it's not used in this code

  return (
    <div
      className={`relative max-w-xs mx-auto shadow-lg rounded-lg overflow-hidden m-4 min-w-55 ${
        theme === "night" ? "bg-night text-white" : "bg-fantasy text-black"
      }`}
    >
      <div className="absolute top-2 right-2">
        <button
          id="btn_update"
          onClick={() => {
            setSelected("UpdateItem");
          }}
        >
          <i className="fas fa-edit"></i>
        </button>
      </div>

      {/* Use the image prop instead of a static logo */}
      <img className="w-full h-auto" src={image || logo} alt={name} />
      <div className="p-4">
        {/* Display the name and description dynamically */}
        <h1 className="font-bold text-xl mb-2">{name}</h1>
        <p className="text-base">{description}</p>
        <div className="mt-4 flex flex-col sm:flex-row">
          {user.role === "P" ? (
            <button className="bg-green-500 px-4 py-2 rounded mr-2 mb-2 sm:mb-0 text-white">
              Buy
            </button>
          ) : (
            <button
              id="btn_item_details"
              onClick={() => {
                setSelected("ItemDetails");
              }}
              className="bg-blue-500 px-4 py-2 rounded text-white"
            >
              See Details
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ItemCard;
