import React, { useState, useEffect } from "react";

export const AdminAccount = ({ selectedRow }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [dateCreated, setDateCreated] = useState("");
  const [status, setStatus] = useState("");

  useEffect(() => {
    if (selectedRow) {
      setUsername(selectedRow.username);
      setEmail(selectedRow.email);
      setDateCreated(selectedRow.date);
      setStatus(selectedRow.status);
    }
  }, [selectedRow]); // Add selectedRow as a dependency

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = () => {
    console.log("Save button clicked");
  };

  const toggleStatus = () => {
    setStatus((prevStatus) => (prevStatus === "Active" ? "Disabled" : "Active"));
  };

  return (
    <div className="flex items-center justify-center h-screen">
      <div className="max-w-4xl mx-auto bg-white p-8 rounded-lg flex shadow-lg relative flex-col md:flex-row">
        {/* Image Holder */}
        <div className="w-full md:w-1/2 p-4 flex flex-col items-center">
          <div className="mb-4">
            {selectedImage ? (
              <img src={selectedImage} alt="Selected" className="w-full h-auto rounded-lg mb-4" />
            ) : (
              <div className="border-2 border-gray-300 h-40 flex items-center justify-center mb-4 rounded-lg">
                <span className="text-gray-500">No Image Selected</span>
              </div>
            )}
          </div>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            className="mb-4"
          />
        </div>

        {/* Account Details */}
        <div className="w-full md:w-1/2 p-4">
          <h2 className="text-2xl font-bold mb-4">Admin Account Details</h2>
          <input
            type="text"
            value={username}
            placeholder="Username"
            onChange={(e) => setUsername(e.target.value)}
            className="w-full mb-4 p-2 rounded-lg border border-gray-300"
          />
          <input
            type="email"
            value={email}
            placeholder="Email"
            onChange={(e) => setEmail(e.target.value)}
            className="w-full mb-4 p-2 rounded-lg border border-gray-300"
          />
          <input
            type="text"
            value={dateCreated}
            placeholder="Date Created"
            onChange={(e) => setDateCreated(e.target.value)}
            className="w-full mb-4 p-2 rounded-lg border border-gray-300"
          />
          <div className="flex items-center mb-4">
            <label className="mr-2">Status:</label>
            <button onClick={toggleStatus} className={`py-1 px-3 rounded ${status === "Active" ? "bg-green-500" : "bg-red-500"} text-white`}>
              {status}
            </button>
          </div>
          <div className="flex justify-between mt-4">
            <button
              onClick={handleSave}
              className="bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded"
            >
              Save
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
