import React from "react";
import { ThemeContext } from "../../context/Theme";
import { useContext, useState, useEffect } from "react";
import { UserContext } from "../../context/User";
import axios from "axios";

const ItemCard = ({ setSelected, item, setSelectedItem }) => {
  const [theme, setTheme] = useContext(ThemeContext);
  const [user, setUser] = useContext(UserContext);
  const [errors, setErrors] = useState([]);
  const [success, setSuccess] = useState(false);

  const authToken = localStorage.getItem("authToken");
  const refreshToken = localStorage.getItem("refreshToken");
  const serverUrl = process.env.REACT_APP_SERVER_URL;

  const handleBuyItem = async (url) => {
    if (user.acc_balance < item.item_value) {
      // Assuming item.price holds the item's price
      setErrors([{ msg: "You do not have enough balance to buy this item." }]);
      return;
    }

    const config = {
      url: `${serverUrl}/item/update`,
      method: "PUT",
      data: {
        id: selectedItem,
      },
    };

    const { res, error, loading } = await API(config);
    if (res) {
      console.log(res);
      setUser(res.data.account);
      setSuccess(true);
      setErrors(["Item updated successfully"]);
    }
    if (error) console.log(error);

    useEffect(() => {
      const fetchItems = async () => {
        try {
          setErrors([]);
          setSuccess(false);

          await axios
            .get(`${serverUrl}/item/itemdetails?id=${selectedItem}`, {
              headers: {
                "x-auth-token": authToken,
                "x-refresh-token": refreshToken,
              },
            })
            .then((response) => {
              setItems(response.data.item);
              setUser(response.data.account);
              setSuccess(true); // Set success only if the request succeeds
            });
        } catch (error) {
          console.error(error);
          setSuccess(false);
          if (error.response && error.response.data.errors) {
            setErrors(error.response.data.errors.map((err) => err.msg));
          } else {
            setErrors(["An unexpected error occurred."]); // Generic fallback
          }
        }
      };

      fetchItems();

      // Optionally return a cleanup function
      return () => {
        setItems([]); // Example cleanup, adjust as needed
      };
    }, []);
  };

  return (
    <div
      className={`relative max-w-xs mx-auto shadow-lg rounded-lg overflow-hidden m-4 w-60 h-80 bg-${theme}`}
    >
      <div className="absolute top-2 right-2">
        {user.role == "P" ? (
          <button
            style={{ display: "none" }}
            id="btn_update"
            onClick={() => {
              setSelectedItem(item.item_id);
              setSelected("UpdateItem");
            }}
          >
            <i className="fas fa-edit"></i>
          </button>
        ) : (
          <button
            id="btn_update"
            onClick={() => {
              setSelectedItem(item.item_id);
              setSelected("UpdateItem");
            }}
          >
            <i className="fas fa-edit"></i>
          </button>
        )}
      </div>

      <img
        className="w-full h-52 object-cover"
        src={item && item.item_sprite}
        alt="card"
      />
      <div className="p-4">
        <h1 className="font-bold text-xl mb-2">{item.name}</h1>
        <p className="text-base">{item.item_desc}</p>
        <div className="mt-4 flex flex-col sm:flex-row">
          {user.role === "P" ? (
            <button
              onClick={() => {
                handleBuyItem(item.item_id);
              }}
              className="bg-green-500 px-4 py-2 rounded mr-2 mb-2 sm:mb-0 text-white"
            >
              Buy
            </button>
          ) : (
            <button
              id="btn_item_details"
              onClick={() => {
                setSelectedItem(item.item_id);
                setSelected("ItemDetails");
              }}
              className="bg-blue-500 px-4 py-2 rounded text-white"
            >
              See Details
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ItemCard;
