import { React, useEffect, useState } from "react";
import logo from "../assets/logo1.png";

function NavBar2({ theme, toggleTheme }) {
  const [scrolled, setScrolled] = useState(false);
  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      if (offset > 0) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div
      className={`fixed navbar bg-base-100 ${
        scrolled ? "border-b-2 border-gray-200" : ""
      }`}
    >
      <div className="flex-1 navbar-start">
        <a className="btn btn-ghost text-xl">
          <img className="h-7 w-7 bg-contain " src={logo} />
          Pugad Maharlika
        </a>
      </div>
      <div className="-center"></div>
      <div className="flex-none gap-3 navbar-end">
        <button className="btn btn-outline">Sign in</button>
        <button onClick={toggleTheme} className="btn btn-square btn-ghost">
          {theme === "night" ? (
            <i class="fa-regular fa-moon"></i>
          ) : (
            <i class="fa-regular fa-sun"></i>
          )}
        </button>
      </div>
    </div>
  );
}

export default NavBar2;
