import { React, useState, useEffect } from "react";
import NavBar from "../components/NavBar";
import DrawerButton from "../components/ui/DrawerButton";
import logo from "../assets/logo1.png";

function Main({ theme, toggleTheme }) {
  const [selected, setSelected] = useState("Dashboard");
  const [sideBarOpen, setSideBarOpen] = useState(true);
  const toggleSideBar = () => {
    setSideBarOpen(!sideBarOpen);
  };
  const handleSelectedButton = (title) => {
    setSelected(title);
  };

  useEffect(() => {
    const screenWidth = window.innerWidth;
    if (screenWidth <= 768) setSideBarOpen(false);
  }, []);

  return (
    <>
      <div className="flex flex-row w-screen ">
        <div
          className={`relative flex-1 h-svh flex-col bg-base-100 border-r-2 border-gray-200 text-fantasy  w-full max-w-[20rem] p-4 shadow-xl shadow-blue-gray-900/5 ${
            sideBarOpen ? "flex min-w-[240px]" : "w-20"
          }`}
        >
          <div className="mb-2 px-2 py-4">
            <h5
              className={`flex gap-2 antialiased tracking-normal font-sans text-lg font-semibold leading-snug ${
                theme === "night" ? "text-fantasy " : "text-night "
              }`}
            >
              <img
                className="h-7 w-7 btn-square bg-contain  "
                alt="Pugad Maharlika Icon"
                src={logo}
              />
              {sideBarOpen ? "Pugad Maharlika" : ""}
            </h5>
          </div>
          <nav className="flex flex-col gap-1 p-0 text-base">
            <DrawerButton
              icon={<i className="fa-solid fa-house-chimney"></i>}
              selected={selected}
              theme={theme}
              sideBarOpen={sideBarOpen}
              title={"Dashboard"}
              handleSelectedButton={handleSelectedButton}
            />
            <DrawerButton
              icon={<i className="fa-solid fa-sack-dollar"></i>}
              selected={selected}
              theme={theme}
              sideBarOpen={sideBarOpen}
              title={"Offers"}
              handleSelectedButton={handleSelectedButton}
            />
            <DrawerButton
              icon={<i className="fa-solid fa-bell"></i>}
              selected={selected}
              theme={theme}
              sideBarOpen={sideBarOpen}
              title={"Notification"}
              handleSelectedButton={handleSelectedButton}
            />
            <DrawerButton
              icon={<i className="fa-solid fa-file-lines"></i>}
              selected={selected}
              theme={theme}
              sideBarOpen={sideBarOpen}
              title={"Transaction"}
              handleSelectedButton={handleSelectedButton}
            />
            <DrawerButton
              icon={<i className="fa-solid fa-user"></i>}
              selected={selected}
              theme={theme}
              sideBarOpen={sideBarOpen}
              title={"Profile"}
              handleSelectedButton={handleSelectedButton}
            />
          </nav>
          <button
            onClick={toggleTheme}
            className="btn btn-square self-end mt-auto btn-outline"
          >
            {theme === "night" ? (
              <i className="fa-regular fa-moon"></i>
            ) : (
              <i className="fa-regular fa-sun"></i>
            )}
          </button>
        </div>
        <div className="flex flex-col h-svh w-svw">
          <NavBar
            theme={theme}
            toggleTheme={toggleTheme}
            toggleSideBar={toggleSideBar}
          />
          <div className="relative flex-2"></div>
        </div>
      </div>
    </>
  );
}

export default Main;
