import React, { useState } from 'react';

const AddAdminAccount = () => {
  const [imageSrc, setImageSrc] = useState('https://via.placeholder.com/150');
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  
  const [isEditing, setIsEditing] = useState(false);

  // Handle changing the image (preview functionality)
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setImageSrc(event.target.result); // Preview new selection
      };
      reader.readAsDataURL(file);
    }
  };

  // Handle form data changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="container mx-auto p-4 max-w-lg bg-white rounded-lg shadow-md">
      {/* Profile image section */}
      <div className="flex justify-center mb-4">
        <div className="w-32 h-32 rounded-full overflow-hidden border">
          <img src={imageSrc} alt="Admin" className="w-full h-full object-cover" />
        </div>
      </div>

      {/* Select image button */}
      <div className="flex justify-center mb-4">
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          className="hidden"
          id="imageInput"
        />
        <label
          htmlFor="imageInput"
          className="cursor-pointer px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
        >
          Select Image
        </label>
      </div>

      {/* Form section */}
      <div className="form-group">
        <label>Username:</label>
        <input
          type="text"
          name="username"
          placeholder="Enter name..."
          value={formData.username}
          onChange={handleChange}
          className="w-full p-2 border rounded mb-4"
        />
      </div>

      <div className="form-group">
        <label>Email:</label>
        <input
          type="email"
          name="email"
          placeholder="Enter email..."
          value={formData.email}
          onChange={handleChange}
          className="w-full p-2 border rounded mb-4"
        />
      </div>

      <div className="flex justify-between">
        <div className="form-group mr-2">
          <label>Password:</label>
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>

        <div className="form-group ml-2">
          <label>Confirm Password:</label>
          <input
            type="password"
            name="confirmPassword"
            placeholder="Password"
            value={formData.confirmPassword}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
      </div>

      {/* Add Account button */}
      <div className="flex justify-end mt-4">
        <button
          onClick={() => console.log('Form submitted:', formData)}
          className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
        >
          Add Account
        </button>
      </div>
    </div>
  );
};

export default AddAdminAccount;
