import { React, useState, useEffect } from "react";
import NavBar from "../components/NavBar";
import logo from "../assets/logo1.png";

function Main({ theme, toggleTheme }) {
  const [sideBarOpen, setSideBarOpen] = useState(true);
  const [winSize, setWinSize] = useState(false);
  const toggleSideBar = () => {
    setSideBarOpen(!sideBarOpen);
  };
  useEffect(() => {
    const screenWidth = window.innerWidth;
    if (screenWidth <= 768) setSideBarOpen(false);
    console.log(screenWidth);
  }, [window.innerWidth]);

  return (
    <>
      <div className="flex flex-row w-screen ">
        <div
          className={`relative flex-1 h-svh flex-col bg-clip-border rounded-xl text-fantasy  w-full max-w-[20rem] p-4 shadow-xl shadow-blue-gray-900/5 ${
            sideBarOpen ? "flex" : "hidden"
          }`}
        >
          <div className="mb-2 p-4">
            <h5 className="flex gap-2 antialiased tracking-normal font-sans text-lg font-semibold leading-snug text-gray-900">
              <img className="h-7 w-7 bg-contain  " src={logo} />
              Pugad Maharlika
            </h5>
          </div>
          <nav className="flex flex-col gap-1 min-w-[240px] p-2 font-sans text-base font-normal text-gray-700">
            <div
              role="button"
              tabindex="0"
              className="flex items-center w-full p-3 rounded-lg text-start leading-tight transition-all hover:bg-blue-50 hover:bg-opacity-80 focus:bg-blue-50 focus:bg-opacity-80 active:bg-gray-50 active:bg-opacity-80 hover:text-blue-900 focus:text-blue-900 active:text-blue-900 outline-none"
            >
              <div className="grid place-items-center mr-4">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 24 24"
                  fill="currentColor"
                  aria-hidden="true"
                  className="h-5 w-5"
                >
                  <path
                    fill-rule="evenodd"
                    d="M2.25 2.25a.75.75 0 000 1.5H3v10.5a3 3 0 003 3h1.21l-1.172 3.513a.75.75 0 001.424.474l.329-.987h8.418l.33.987a.75.75 0 001.422-.474l-1.17-3.513H18a3 3 0 003-3V3.75h.75a.75.75 0 000-1.5H2.25zm6.04 16.5l.5-1.5h6.42l.5 1.5H8.29zm7.46-12a.75.75 0 00-1.5 0v6a.75.75 0 001.5 0v-6zm-3 2.25a.75.75 0 00-1.5 0v3.75a.75.75 0 001.5 0V9zm-3 2.25a.75.75 0 00-1.5 0v1.5a.75.75 0 001.5 0v-1.5z"
                    clip-rule="evenodd"
                  ></path>
                </svg>
              </div>
              Blocks
            </div>
          </nav>
        </div>
        <div className="flex flex-col h-svh w-svw">
          <NavBar
            theme={theme}
            toggleTheme={toggleTheme}
            toggleSideBar={toggleSideBar}
          />
          <div className="relative flex-2"></div>
        </div>
      </div>
    </>
  );
}

export default Main;
