import React, { useState } from 'react';

const ImageUploader = () => {
  const [image, setImage] = useState(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    
    reader.onload = () => {
      setImage(reader.result);
    };

    reader.readAsDataURL(file);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-200">
      <div className="bg-white p-10 rounded-lg shadow-lg">
        <h1 className="text-3xl font-bold mb-4">Image Uploader</h1>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="mb-4"
        />
        {image && (
          <div className="mb-4">
            <img src={image} alt="Uploaded" className="rounded-lg" style={{ maxWidth: '100%' }} />
          </div>
        )}
        <input type="text" placeholder="Title 1" className="w-full border border-gray-300 p-2 rounded-lg mb-2" />
        <input type="text" placeholder="Title 2" className="w-full border border-gray-300 p-2 rounded-lg mb-2" />
        <input type="text" placeholder="Title 3" className="w-full border border-gray-300 p-2 rounded-lg mb-2" />
        <input type="text" placeholder="Title 4" className="w-full border border-gray-300 p-2 rounded-lg mb-4" />
        <button className="w-full rounded-3xl bg-black px-6 py-2 text-xl font-medium uppercase text-white">
          Submit
        </button>
      </div>
    </div>
  );
};

export default ImageUploader;
