import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AlertsContext } from "../../context/Alerts";
import { UserContext } from "../../context/User";
import { SuccessContext } from "../../context/Success";
import ConfirmationDialog from "../../components/ConfirmationDialog";
import axios from "axios";

const serverUrl = process.env.REACT_APP_SERVER_URL;

const accvalues = [
  { id: 1, username: "OhMama", email: "johny@gmail.com", date: "2024-01-28T00:30", status: "Active" },
  { id: 2, username: "OhMuscle", email: "masku@gmail.com", date: "2024-01-25T22:40", status: "Active" },
  { id: 3, username: "Alahooooo!", email: "abdul@gmail.com", date: "2024-01-20T22:40", status: "Disabled" },
  { id: 4, username: "Lalalala", email: "wsmith@gmail.com", date: "2024-01-15T15:29", status: "Disabled" },
  { id: 5, username: "Yeaaaahh!", email: "ESing@gmail.com", date: "2024-01-14T22:40", status: "Active" }
];

const AdminAccount = ({ theme }) => {
  const [profile, setProfile] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [newpassword, setNewPassword] = useState("");
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const [user, setUser] = useContext(UserContext);
  const authToken = localStorage.getItem("authToken");
  const refreshToken = localStorage.getItem("refreshToken");
  const navigate = useNavigate();

  useEffect(() => {
    const selectedAccount = accvalues.find(account => account.id === 1);
    if (selectedAccount) {
      setUsername(selectedAccount.username);
      setUser(selectedAccount); // Setting user data to context
    }
  }, []);

  const handleProfile = () => {
    setErrors([]);
    setSuccess(false);
    if (profile === "") {
      setSuccess(false);
      setErrors(["No file selected. Please select an image file"]);
      return;
    }

    const allowedTypes = ["image/png", "image/jpeg", "image/jpg"];
    if (!allowedTypes.includes(profile.type.toLowerCase())) {
      setSuccess(false);
      setErrors(["Invalid file type. Only PNG, JPG, and JPEG are allowed"]);
      return;
    }

    const maxSize = 5 * 1024 * 1024; // 5 MB in bytes
    if (profile.size > maxSize) {
      setSuccess(false);
      setErrors(["File size exceeds 5 MB. Please choose a smaller file."]);
      return;
    }
    
    const formData = new FormData();
    formData.append("profile", profile);
    
    axios
      .put(`${serverUrl}/profile/update-profile`, formData, {
        headers: {
          "x-auth-token": authToken,
          "x-refresh-token": refreshToken,
        },
      })
      .then((response) => {
        setSuccess(true);
        setErrors(["Success! Profile picture has been changed"]);
        setUser(response.data.account);
      })
      .catch((error) => {
        console.log(error);
        setSuccess(false);
        setErrors(error.response.data.errors.map((error) => error.msg));
      });
  };

  const handlePutRequest = async (route, data, successMsg) => {
    axios
      .put(`${serverUrl}${route}`, data, {
        headers: {
          "x-auth-token": authToken,
          "x-refresh-token": refreshToken,
        },
      })
      .then((response) => {
        setSuccess(true);
        setErrors([successMsg]);
        setUser(response.data.account);
      })
      .catch((error) => {
        console.log(error);
        setSuccess(false);
        setErrors(error.response.data.errors.map((error) => error.msg));
      });
  };

  const handleUsername = async () => {
    if (!username) {
      setSuccess(false);
      setErrors(["No username entered"]);
      return;
    }
    await handlePutRequest(
      "/profile/update-username",
      { username: username },
      "Success! Username has been changed"
    );
  };

  const handlePassword = async () => {
    if (!password || !newpassword) {
      setSuccess(false);
      setErrors(["Current or new password is not filled"]);
      return;
    }
    await handlePutRequest(
      "/profile/update-password",
      { currentpassword: password, newpassword: newpassword },
      "Success! Password has been changed"
    );
  };

  const handleDisable = async () => {
    await handlePutRequest("/profile/disable-account", {}, "Success! Account is now disabled");

    localStorage.setItem("user", null);
    localStorage.setItem("authToken", null);
    localStorage.setItem("refreshToken", null);
    navigate("/");
  };

  return (
    <>
      {/* Logout Dialog */}
      <ConfirmationDialog
        theme={theme}
        handleAction={() => {
          handleDisable();
        }}
        id={"disable_account_dialog"}
        message={"Are you sure you want to DISABLE your account?"}
        buttonText={"Confirm"}
        is_danger={true}
      />
      <div
        className={`col-span-8 overflow-hidden rounded-lg text-xs md:text-md w-64 px-8 sm:w-full ${
          theme === "night" ? "bg-night text-white" : "bg-fantasy text-black"
        }`}
      >
        <div className="pt-4">
          <h1 className="py-2 font-semibold">Admin Account</h1>
        </div>
        <hr className="mt-4 mb-8" />
        <p className="py-2 font-semibold">Profile Picture</p>
        <div className="flex flex-col md:flex-row sm:items-left sm:justify-between">
          <input type="file" onChange={(e) => setProfile(e.target.files[0])} />
          <button
            onClick={() => {
              handleProfile();
            }}
            className="inline-flex font-semibold text-blue-600 hover:underline decoration-2"
          >
            Change
          </button>
        </div>
        <hr className="mt-4 mb-8" />
        <p className="py-2 font-semibold">Username</p>
        <div className="flex flex-col md:flex-row md:items-center gap-1 sm:justify-between">
          <p className="">
            Your username is <strong>{user.username}</strong>
          </p>
          <div className="relative flex overflow-hidden rounded-md border-2 transition focus-within:border-blue-600">
            <input
              type="text"
              id="username"
              className="w-full flex-shrink appearance-none border-gray-300 bg-white py-2 px-4 text-base text-gray-700 placeholder-gray-400 focus:outline-none"
              placeholder={user.username}
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <button
            onClick={handleUsername}
            className="inline-flex font-semibold text-blue-600 hover:underline decoration-2"
          >
            Change
          </button>
        </div>
        <hr className="mt-4 mb-8" />
        <p className="py-2 font-semibold">Email Address</p>
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
          <p className="">
            Your email address is <strong>{user.email}</strong>
          </p>
        </div>
        <hr className="mt-4 mb-8" />
        <p className="py-2 font-semibold">Password</p>
        <div className="flex items-center">
          <div className="flex flex-col space-y-2 sm:flex-row sm:space-y-0 sm:space-x-3">
            <label htmlFor="login-password">
              <span className="">Current Password</span>
              <div className="relative flex overflow-hidden rounded-md border-2 transition focus-within:border-blue-600">
                <input
                  type="password"
                  id="current-login-password"
                  className="w-full flex-shrink appearance-none border-gray-300 bg-white py-2 px-4 text-base text-gray-700 placeholder-gray-400 focus:outline-none"
                  placeholder="***********"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>
            </label>
            <label htmlFor="login-password">
              <span className="">New Password</span>
              <div className="relative flex overflow-hidden rounded-md border-2 transition focus-within:border-blue-600">
                <input
                  type="password"
                  id="new-login-password"
                  className="w-full flex-shrink appearance-none border-gray-300 bg-white py-2 px-4 text-base text-gray-700 placeholder-gray-400 focus:outline-none"
                  placeholder="***********"
                  value={newpassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
              </div>
            </label>
            <button
              onClick={handlePassword}
              className="inline-flex font-semibold text-blue-600 hover:underline decoration-2"
            >
              Change
            </button>
          </div>
        </div>
        <hr className="mt-4 mb-8" />
        <button
          onClick={() => {
            // handleDisable logic
          }}
          className="inline-flex font-semibold text-red-600 hover:underline decoration-2"
        >
          Disable Account
        </button>
      </div>
    </>
  );
};

export default AdminAccount;
