import React, { useState, useEffect, useContext } from "react";
import shane from "/Github/Pugad Testing/Pugad-Maharlika-Frontend/client/src/assets/James-Reid.jpg";
import { SuccessContext } from "../../context/Success";
import { AlertsContext } from "../../context/Alerts";
import API from "../../service/API";

export const AdminAccount = ({ setSelected, selectedadmin }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [dateCreated, setDateCreated] = useState("");
  const [status, setStatus] = useState(true); // true for "Active", false for "Disabled"
  const [wins, setWins] = useState("");
  const [loses, setLoses] = useState("");
  const [gold, setGold] = useState("");
  const [perlas, setPerlas] = useState("");
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const serverUrl = process.env.REACT_APP_SERVER_URL;
  const [useraccount, setUserAccount] = useState({});

  useEffect(() => {
    const handleViewAdmins = async () => {
      console.log(selectedadmin);
      setErrors([]);
      setSuccess(false);
      const config = {
        url: `${serverUrl}/account/view`,
        method: "POST",
        data: { id: selectedadmin },
      };

      const { res, error } = await API(config);
      if (res) {
        setUserAccount(res.data.useraccount);
      }
      if (error) {
        setErrors(error.response.data.errors.map((error) => error.msg));
      }
    };
    handleViewAdmins();
  }, []); // Dependencies to rerun effect only when these values change

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = () => {
    console.log("Save button clicked");
  };

  const handleToggleStatus = () => {
    setStatus((prevStatus) => {
      const newStatus = !prevStatus;
      console.log(newStatus ? "Account Activated" : "Account Disabled");
      return newStatus;
    });
  };

  return (
    <div className="flex items-center justify-center h-screen">
      <div className="max-w-4xl mx-auto bg-white p-8 rounded-lg flex shadow-lg relative flex-col md:flex-row">
        {/* Back Button */}
        <button
          onClick={() => setSelected("Admin")}
          className="absolute top-4 left-4 text-black hover:text-gray-600"
        >
          <i className="fa-solid fa-circle-chevron-left text-3xl"></i>
        </button>

        {/* Image Holder */}
        <div className="w-full md:w-1/2 p-4 flex flex-col items-center">
          <div className="mb-4">
            {useraccount && (
              <img
                src={useraccount.acc_profile}
                alt="Selected"
                className="w-full h-auto rounded-lg mb-4"
              />
            )}
          </div>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            className="mb-4"
          />
        </div>

        {/* Account Details */}
        <div className="w-full md:w-1/2 p-4">
          <h2 className="text-2xl font-bold mb-4">Admin Account Details</h2>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1" htmlFor="username">
              Username
            </label>
            <input
              id="username"
              type="text"
              value={useraccount.acc_username}
              placeholder="Username"
              onChange={(e) => setUsername(e.target.value)}
              className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1" htmlFor="email">
              Email
            </label>
            <input
              id="email"
              type="email"
              value={useraccount.acc_email}
              placeholder="Email"
              onChange={(e) => setEmail(e.target.value)}
              className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1" htmlFor="wins">
              Wins
            </label>
            <input
              id="wins"
              type="text"
              value={wins}
              placeholder="Wins"
              className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1" htmlFor="loses">
              Loses
            </label>
            <input
              id="loses"
              type="text"
              value={loses}
              placeholder="Loses"
              className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1" htmlFor="winrate">
              Win Rate
            </label>
            <input
              id="winrate"
              type="text"
              value={(wins / (wins + loses)) * 100}
              placeholder="Win Rate"
              className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1" htmlFor="gold">
              Gold
            </label>
            <input
              id="gold"
              type="text"
              value={gold}
              placeholder="Gold"
              onChange={(e) => setGold(e.target.value)}
              className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1" htmlFor="Perlas">
              Perlas
            </label>
            <input
              id="perlas"
              type="text"
              value={username}
              placeholder="Perlas"
              onChange={(e) => setPerlas(e.target.value)}
              className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1" htmlFor="dateCreated">
              Date Created
            </label>
            <input
              disabled
              id="dateCreated"
              type="datetime-local"
              value={dateCreated}
              onChange={(e) => setDateCreated(e.target.value)}
              className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            />
          </div>

          {/* Toggle Status Button */}
          <div className="flex justify-end items-center mb-4">
            <button
              onClick={handleToggleStatus}
              className={`py-2 px-4 rounded text-white ${
                status
                  ? "bg-red-500 hover:bg-red-700"
                  : "bg-green-500 hover:bg-green-700"
              }`}
            >
              {status ? "Deactivate" : "Activate"}
            </button>
          </div>

          {/* Save Button */}
          <div className="flex justify-end mt-4">
            <button
              onClick={handleSave}
              className="bg-green-500 hover:bg-green-700 text-white py-2 px-4 rounded"
            >
              Save
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
