import React, { useEffect, useState } from "react";

export const AdminAccount = ({ selectedAccount }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [dateCreated, setDateCreated] = useState("");
  const [status, setStatus] = useState("");

  useEffect(() => {
    if (selectedAccount) {
      setUsername(selectedAccount.username);
      setEmail(selectedAccount.email);
      setDateCreated(selectedAccount.date);
      setStatus(selectedAccount.status);
    }
  }, [selectedAccount]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="p-6 bg-white rounded shadow">
      <h1 className="text-2xl font-semibold mb-4">Admin Account Details</h1>
      
      <div className="mb-4">
        {selectedImage && (
          <img src={selectedImage} alt="Selected" className="w-32 h-32 rounded-full" />
        )}
        <input type="file" onChange={handleImageChange} className="mt-2" />
      </div>

      <div className="mb-4">
        <label className="block text-sm font-medium">Username:</label>
        <input 
          type="text" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
          className="mt-1 block w-full border rounded p-2" 
        />
      </div>
      
      <div className="mb-4">
        <label className="block text-sm font-medium">Email:</label>
        <input 
          type="email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          className="mt-1 block w-full border rounded p-2" 
        />
      </div>

      <div className="mb-4">
        <label className="block text-sm font-medium">Date Created:</label>
        <input 
          type="text" 
          value={dateCreated} 
          readOnly 
          className="mt-1 block w-full border rounded p-2 bg-gray-100" 
        />
      </div>

      <div className="mb-4">
        <label className="block text-sm font-medium">Status:</label>
        <select 
          value={status} 
          onChange={(e) => setStatus(e.target.value)} 
          className="mt-1 block w-full border rounded p-2"
        >
          <option value="Active">Active</option>
          <option value="Disabled">Disabled</option>
        </select>
      </div>

      <button 
        onClick={() => {
          // Save the changes or submit form logic
          console.log("Account details saved!");
        }} 
        className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
      >
        Save Changes
      </button>
    </div>
  );
};
