import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AlertsContext } from "../../context/Alerts";
import { UserContext } from "../../context/User";
import { SuccessContext } from "../../context/Success";
import ConfirmationDialog from "../../components/ConfirmationDialog";
import axios from "axios";
import useAuthCheck from "../../hooks/useAuthCheck";

const AdminAccount = ({ theme }) => {
  const [profile, setProfile] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [newpassword, setNewPassword] = useState("");
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const [user, setUser] = useContext(UserContext);
  const navigate = useNavigate();

  const handleProfile = () => {
    setErrors([]);
    setSuccess(false);
    if (profile === "") {
      setErrors(["No file selected. Please select an image file"]);
      return;
    }

    // Check file type (case-insensitive)
    const allowedTypes = ["image/png", "image/jpeg", "image/jpg"];
    if (!allowedTypes.includes(profile.type.toLowerCase())) {
      setErrors(["Invalid file type. Only PNG, JPG, and JPEG are allowed"]);
      return;
    }

    // Check file size (less than or equal to 5 MB)
    const maxSize = 5 * 1024 * 1024; // 5 MB in bytes
    if (profile.size > maxSize) {
      setErrors(["File size exceeds 5 MB. Please choose a smaller file."]);
      return;
    }

    // Commented out the API call to update the profile
    /*
    const formData = new FormData();
    formData.append("profile", profile);
    axios
      .put(`${serverUrl}/profile/update-profile`, formData, {
        headers: {
          "x-auth-token": authToken,
          "x-refresh-token": refreshToken,
        },
      })
      .then((response) => {
        setSuccess(true);
        setErrors(["Success! profile picture has been changed"]);
        setUser(response.data.account);
      })
      .catch((error) => {
        console.log(error);
        setErrors(error.response.data.errors.map((error) => error.msg));
      });
    */
  };

  const handleUsername = () => {
    if (!username) {
      setErrors(["No username entered"]);
      return;
    }
    // Commented out the API call to update the username
    /*
    axios
      .put(`${serverUrl}/profile/update-username`, { username }, {
        headers: {
          "x-auth-token": authToken,
          "x-refresh-token": refreshToken,
        },
      })
      .then((response) => {
        setSuccess(true);
        setErrors(["Success! username has been changed"]);
        setUser(response.data.account);
      })
      .catch((error) => {
        console.log(error);
        setErrors(error.response.data.errors.map((error) => error.msg));
      });
    */
  };

  const handlePassword = () => {
    if (!password || !newpassword) {
      setErrors(["Current or new password is not filled"]);
      return;
    }
    // Commented out the API call to update the password
    /*
    axios
      .put(`${serverUrl}/profile/update-password`, { currentpassword: password, newpassword }, {
        headers: {
          "x-auth-token": authToken,
          "x-refresh-token": refreshToken,
        },
      })
      .then((response) => {
        setSuccess(true);
        setErrors(["Success! password has been changed"]);
        setUser(response.data.account);
      })
      .catch((error) => {
        console.log(error);
        setErrors(error.response.data.errors.map((error) => error.msg));
      });
    */
  };

  const handleDisable = () => {
    // Commented out the API call to disable the account
    /*
    axios
      .put(`${serverUrl}/profile/disable-account`, {}, {
        headers: {
          "x-auth-token": authToken,
          "x-refresh-token": refreshToken,
        },
      })
      .then((response) => {
        setSuccess(true);
        setErrors(["Success! account is now disabled"]);
        localStorage.clear();
        navigate("/");
      })
      .catch((error) => {
        console.log(error);
        setErrors(error.response.data.errors.map((error) => error.msg));
      });
    */
    localStorage.clear(); // Clear local storage to simulate disabling
    navigate("/"); // Navigate back to the homepage
  };

  return (
    <>
      {/* Logout Dialog */}
      <ConfirmationDialog
        theme={theme}
        handleAction={handleDisable}
        id={"disable_account_dialog"}
        message={"Are you sure you want to DISABLE your account?"}
        buttonText={"Confirm"}
        is_danger={true}
      />
      <div
        className={`col-span-8 overflow-hidden rounded-lg text-xs md:text-md w-64 px-8 sm:w-full ${
          theme === "night" ? "bg-night text-white " : "bg-fantasy text-black"
        }`}
      >
        <div className="pt-4">
          <h1 className="py-2 font-semibold">Account Profile</h1>
        </div>
        <hr className="mt-4 mb-8" />
        <p className="py-2 font-semibold">Profile Picture</p>
        <div className="flex flex-col md:flex-row sm:items-left sm:justify-between">
          <input type="file" onChange={(e) => setProfile(e.target.files[0])} />
          <button
            onClick={handleProfile}
            className="inline-flex font-semibold text-blue-600 hover:underline decoration-2"
          >
            Change
          </button>
        </div>
        <hr className="mt-4 mb-8" />
        <p className="py-2 font-semibold">Username</p>
        <div className="flex flex-col md:flex-row md:items-center gap-1 sm:justify-between">
          <p>
            Account Username: <strong>{user.username}</strong>
          </p>
          <div className="relative flex overflow-hidden rounded-md border-2 transition focus-within:border-blue-600">
            <input
              type="text"
              id="username"
              className="w-full flex-shrink appearance-none border-gray-300 bg-white py-2 px-4 text-base text-gray-700 placeholder-gray-400 focus:outline-none"
              placeholder={user.username}
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <button
            onClick={handleUsername}
            className="inline-flex font-semibold text-blue-600 hover:underline decoration-2"
          >
            Change
          </button>
        </div>
        <hr className="mt-4 mb-8" />
        <p className="py-2 font-semibold">Email Address</p>
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
          <p>
            Email Address <strong>{user.email}</strong>
          </p>
        </div>
        <hr className="mt-4 mb-8" />
        <p className="py-2 font-semibold">Password</p>
        <div className="flex items-center">
          <div className="flex flex-col space-y-2 sm:flex-row sm:space-y-0 sm:space-x-3">
            <label htmlFor="login-password">
              <span>Current Password</span>
              <div className="relative flex overflow-hidden rounded-md border-2 transition focus-within:border-blue-600">
                <input
                  type="password"
                  id="current-login-password"
                  className="w-full flex-shrink appearance-none border-gray-300 bg-white py-2 px-4 text-base text-gray-700 placeholder-gray-400 focus:outline-none"
                  placeholder="***********"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>
            </label>
            <label htmlFor="login-password">
              <span>New Password</span>
              <div className="relative flex overflow-hidden rounded-md border-2 transition focus-within:border-blue-600">
                <input
                  type="password"
                  id="new-login-password"
                  className="w-full flex-shrink appearance-none border-gray-300 bg-white py-2 px-4 text-base text-gray-700 placeholder-gray-400 focus:outline-none"
                  placeholder="***********"
                  value={newpassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
              </div>
            </label>
          </div>
        </div>

        <button
          onClick={handlePassword}
          className="mt-4 rounded-lg bg-blue-600 px-4 py-2 text-white"
        >
          Save Password
        </button>
        <hr className="mt-4 mb-8" />

        <div className="mb-10">
          <p className="py-2 font-semibold">Disable Account</p>
          <p className="inline-flex items-center rounded-full bg-rose-100 px-4 py-1 text-rose-700">
            Deactivate your account, you can always create a new one
          </p>
          <button
            onClick={handleDisable}
            className="mt-4 inline-flex rounded-lg bg-red-600 px-4 py-2 text-white"
          >
            Disable Account
          </button>
        </div>
      </div>
    </>
  );
};

export default AdminAccount;
