import React from "react";
import { ThemeContext } from "../../context/Theme";
import { useContext, useState } from "react";

export const Invoice = ({ setSelected, transactions }) => {
  const [theme] = useContext(ThemeContext);
  const [items, setItems] = useState("");
  const [user, setUser] = useContext(UserContext);
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const authToken = localStorage.getItem("authToken");
  const refreshToken = localStorage.getItem("refreshToken");
  const serverUrl = process.env.REACT_APP_SERVER_URL;

  useEffect(() => {
    const fetchItems = async () => {
      try {
        setErrors([]);
        setSuccess(false);

        await axios
          .get(`${serverUrl}/item/itemdetails?id=${selectedItem}`, {
            headers: {
              "x-auth-token": authToken,
              "x-refresh-token": refreshToken,
            },
          })
          .then((response) => {
            console.log(response);
            setItems(response.data.item);
            setUser(response.data.account);
            setSuccess(true); // Set success only if the request succeeds
          });
      } catch (error) {
        console.error(error);
        setSuccess(false);
        if (error.response && error.response.data.errors) {
          setErrors(error.response.data.errors.map((err) => err.msg));
        } else {
          setErrors(["An unexpected error occurred."]); // Generic fallback
        }
      }
    };

    fetchItems();

    // Optionally return a cleanup function
    return () => {
      setItems([]); // Example cleanup, adjust as needed
    };
  }, []); // Dependencies to rerun effect only when these values change

  return (
    <>
      <div className="relative bg-white border rounded-lg shadow-lg px-6 py-8 flex-grow min-w-20 mx-auto mt-8">
        {/* Upper left button */}
        <button
          id="btn_back"
          onClick={() => {
            setSelected("Transactions");
          }}
          className="rounded-lg px-1"
        >
          <i className="fa-solid fa-circle-chevron-left text-3xl"></i>
        </button>

        {/* Upper right button */}
        <button
          className="absolute top-4 right-4 bg-green-500 text-white px-2 rounded"
          onClick={() => console.log("Right Button Clicked")}
        >
          <i className="fa-solid fa-arrow-down text-3xl"></i>
        </button>

        <h1 className="font-bold text-2xl my-4 text-center text-blue-600">
          Pugad Maharlika
        </h1>
        <hr className="mb-2" />

        <div className="flex justify-between mb-6">
          <h1 className="text-lg font-bold">Invoice</h1>
          <div className="text-gray-700">
            <div>Date: 01/05/2023</div>
            <div>Invoice #: INV12345</div>
          </div>
        </div>

        <div className="mb-8">
          <h2 className="text-lg font-bold mb-4">Bill To:</h2>
          <div className="text-gray-700 mb-2">John Doe</div>
          <div className="text-gray-700 mb-2">123 Main St.</div>
          <div className="text-gray-700 mb-2">Anytown, USA 12345</div>
          <div className="text-gray-700">johndoe@example.com</div>
        </div>

        <table className="w-full mb-8">
          <thead>
            <tr>
              <th className="text-left font-bold text-gray-700">Description</th>
              <th className="text-right font-bold text-gray-700">Amount</th>
            </tr>
          </thead>
          {transactions.map((transaction) => (
            <tr key={transaction.id}>
              <td className="text-left text-gray-700">
                {transaction.item_name}
              </td>
              <td className="text-right text-gray-700">
                ${transaction.item_value.toFixed(2)}
              </td>
            </tr>
          ))}
          <tfoot>
            <tr>
              <td className="text-left font-bold text-gray-700">Total</td>
              <td className="text-right font-bold text-gray-700">$225.00</td>
            </tr>
          </tfoot>
        </table>

        <div className="text-gray-700 mb-2">Thank you for your business!</div>
        <div className="text-gray-700 text-sm">
          Please remit payment within 30 days.
        </div>
      </div>
    </>
  );
};

export default Invoice;
