import React from "react";
import { ThemeContext } from "../../context/Theme";
import { useContext, useState, useEffect } from "react";
import TransactionTable from "../../components/ui/TransactionTable";
import { AlertsContext } from "../../context/Alerts";
import { SuccessContext } from "../../context/Success";
import { UserContext } from "../../context/User";
import API from "../../service/API";
import axios from "axios";

export const Transactions = ({
  setSelected,
  setTransactionSelected,
  transactionSelected,
}) => {
  const [theme] = useContext(ThemeContext);
  const [transactions, setTransactions] = useState([]);
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const [user, setUser] = useContext(UserContext);
  const authToken = localStorage.getItem("authToken");
  const refreshToken = localStorage.getItem("refreshToken");
  const serverUrl = process.env.REACT_APP_SERVER_URL;
  const [search, setSearch] = useState("");
  const [selectedOption, setSelectedOption] = useState("");

  useEffect(() => {
    fetchTransactions();

    return () => {
      setTransactions([]);
    };
  }, []);

  const fetchTransactions = async () => {
    try {
      setErrors([]);
      setSuccess(false);

      await axios
        .get(`${serverUrl}/transaction/view`, {
          headers: {
            "x-auth-token": authToken,
            "x-refresh-token": refreshToken,
          },
        })
        .then((response) => {
          console.log(response.data);
          setTransactions(response.data.transactionData);
          setSuccess(true);
        });
    } catch (error) {
      console.error(error);
      setSuccess(false);
      if (error.response && error.response.data.errors) {
        setErrors(error.response.data.errors.map((err) => err.msg));
      } else {
        setErrors(["An unexpected error occurred."]);
      }
    }
  };

  const handleSearchTransactionHis = async () => {
    if (search == "") {
      fetchTransactions();
      return;
    }
    setErrors([]);
    setSuccess(false);

    if (!search.trim()) {
      return;
    }

    const config = {
      url: `${serverUrl}/transaction/search?his_id=${search}`,
      method: "GET",
      data: {},
    };

    const { res, error } = await API(config);

    if (res) {
      if (res.data.transactionSearchID) {
        setTransactions([res.data.transactionSearchID]);
        setUser(res.data.account);
      } else {
        setErrors(["ID not found"]);
      }
    }

    if (error) {
      console.log(error);
      setErrors([
        error.response?.data?.errors?.map((error) => error.msg) ||
          "An error occurred",
      ]);
    }
  };

  const handleSearchItemOrOffer = async () => {
    if (search === "") {
      fetchTransactions(); // Fetch all items/offers if search is empty
      return;
    }
    setErrors([]);
    setSuccess(false);

    if (!search.trim()) {
      return;
    }

    const config = {
      url: `${serverUrl}/transaction/search/itemtype?query=${search}`,
      method: "GET",
    };

    try {
      const { res, error } = await API(config);

      if (res) {
        if (res.data.results) {
          setTransactions(res.data.transactionSearchItemType);
          setUser(res.data.userInfo);
        } else {
          setErrors(["No results found"]);
        }
      }

      if (error) {
        console.log(error);
        setErrors([
          error.response?.data?.errors?.map((err) => err.msg) ||
            "An error occurred",
        ]);
      }
    } catch (err) {
      setErrors(["Failed to fetch results"]);
      console.error(err);
    }
  };

  const handleSelectChange = (e) => {
    setSelectedOption(e.target.value);
    handleSearchItemOrOffer(); // Trigger search immediately when dropdown changes
  };

  return (
    <>
      <div className="container mx-auto p-4">
        <div
          className={`flex w-full rounded-xl h-16 shadow-md bg-fantasy p-4 pl-4 py-4 font-bold items-center mb-4 ${
            theme === "night" ? "bg-night text-white " : "bg-fantasy text-black"
          }`}
        >
          <h1 className="text-2xl font-bold">Transaction</h1>
        </div>
        <div
          className={`flex flex-col overflow-hidden rounded-lg shadow-lg  text-xs md:text-md w-64 px-8 sm:w-full py-10 mb-5 ${
            theme === "night"
              ? "bg-night  text-white "
              : "bg-fantasy text-black"
          }`}
        >
          <div className="flex">
            <div className="w-full sm:w-full p-1 py-1 px-7 mb-4 sm:mb-0">
              <div className="flex items-center border rounded-md w-full">
                {/* Search Input */}
                <input
                  type="number"
                  placeholder="Search..."
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  className="p-2 w-full rounded-l-md outline-none"
                />
                {/* Search Icon */}
                <button onClick={() => handleSearchTransactionHis()}>
                  <i className="fa-solid fa-magnifying-glass p-2 "></i>
                </button>
              </div>
            </div>
            <select
              className="border border-gray-300 p-2 rounded-r-lg"
              onChange={handleSelectChange}
            >
              <option disabled selected>
                Choose Type
              </option>
              <option>Item</option>
              <option>Offer</option>
            </select>
          </div>

          <TransactionTable
            transactions={transactions}
            setSelected={setSelected}
            setTransactionSelected={setTransactionSelected}
          />
        </div>
      </div>
    </>
  );
};

export default Transactions;
