import React, { useContext, useState, useEffect } from "react";
import { ThemeContext } from "../../context/Theme";
import axios from "axios";
import { AlertsContext } from "../../context/Alerts";
import { UserContext } from "../../context/User";
import { SuccessContext } from "../../context/Success";

export const ItemDetails = ({ setSelected, item_id }) => {
  const [theme, setTheme] = useContext(ThemeContext);
  const [item, setItem] = useState(null);
  const [user, setUser] = useContext(UserContext);
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const authToken = localStorage.getItem("authToken");
  const refreshToken = localStorage.getItem("refreshToken");
  const serverUrl = process.env.REACT_APP_SERVER_URL;

  useEffect(() => {
    const fetchItem = async () => {
      try {
        setErrors([]);
        setSuccess(false);

        // Make the API request with item_id as a parameter
        const response = await axios.get(`${serverUrl}/item/itemdetails`, {
          headers: {
            "x-auth-token": authToken,
            "x-refresh-token": refreshToken,
          },
          params: { id: item_id }, // pass item_id here as query param
        });

        // Set fetched data to state
        setItem(response.data.item);
        setUser(response.data.account);
        setSuccess(true);
      } catch (error) {
        console.error(error);
        setSuccess(false);
        if (error.response && error.response.data.errors) {
          setErrors(error.response.data.errors.map((err) => err.msg));
        } else {
          setErrors(["An unexpected error occurred."]);
        }
      }
    };

    // Fetch item details when item_id changes
    if (item_id) fetchItem();
  }, [item_id, authToken, refreshToken, serverUrl]);

  return (
    <div className="col-span-8 overflow-hidden rounded-lg text-xs md:text-md px-8 sm:w-full h-full">
      <div className="flex justify-between items-center py-4">
        <h1 className="text-3xl font-bold">Item Details</h1>
        <button
          id="btn_back_item_details"
          onClick={() => setSelected("Item")}
          className="rounded-lg px-4"
        >
          <i className="fa-solid fa-circle-chevron-left text-3xl"></i>
        </button>
      </div>

      {/* Only render item details if item data exists */}
      {item ? (
        <div className="mx-auto bg-white rounded-xl shadow-md overflow-hidden w-full">
          <div className="md:flex">
            <div className="md:flex-shrink-0">
              <img
                className="h-48 w-full object-contain m-2"
                src={item.imageUrl}
                alt="Product"
              />
            </div>
            <div className="p-8">
              <div className="uppercase tracking-wide text-sm text-indigo-500 font-semibold">
                {item.name}
              </div>
              <p className="mt-2 text-gray-600">{item.description}</p>
              <div className="mt-4">
                <span className="text-gray-600">Price: {item.value}</span>
              </div>
              <div className="mt-4">
                <span className="text-gray-600">
                  Status: {item.enabled ? "Enabled" : "Disabled"}
                </span>
              </div>
              <div className="mt-4">
                <span className="text-gray-600">
                  Date Created: {item.date_created}
                </span>
              </div>
              <div className="mt-4">
                <span className="text-gray-600">
                  Date Updated: {item.date_updated}
                </span>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <p>Loading item details...</p> // Show loading message if item is null
      )}
    </div>
  );
};

export default ItemDetails;
