import * as React from "react";
import { useEffect } from "react";

function App() {
  const [theme, setTheme] = React.useState("fantasy");
  const toggleTheme = () => {
    setTheme(theme === "night" ? "fantasy" : "night");
  };
  useEffect(() => {
    document.querySelector("html").setAttribute("data-theme", theme);
  }, [theme]);

  return <></>;
}

export default App;
