import React, { useState } from 'react';
import { AdminAccount } from './AdminAccount'; // Adjust the import based on your file structure

const DataTable = ({ setSelected, accvalues }) => {
  const [selectedRow, setSelectedRow] = useState(null);

  const handleRowClick = (row) => {
    setSelectedRow(row); // Set the clicked row as selectedRow
  };

  return (
    <div>
      <div className="overflow-x-auto">
        <table className="min-w-full border border-gray-300">
          <thead className="bg-gray-200">
            <tr>
              <th className="p-2 border">Username</th>
              <th className="p-2 border">Email</th>
              <th className="p-2 border">Date Created</th>
              <th className="p-2 border">Status</th>
            </tr>
          </thead>
          <tbody>
            {accvalues.map((row) => (
              <tr
                key={row.id}
                onClick={() => handleRowClick(row)}
                className="cursor-pointer hover:bg-gray-100"
              >
                <td className="p-2 border">{row.username}</td>
                <td className="p-2 border">{row.email}</td>
                <td className="p-2 border">{row.date}</td>
                <td className="p-2 border">{row.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Render AdminAccount only if a row is selected */}
      {selectedRow && <AdminAccount setSelected={setSelected} selectedRow={selectedRow} />}
    </div>
  );
};

export default DataTable;
