import React from "react";

function Alert({ index, error, errors, setErrors, theme }) {
  return (
    <div
      key={index}
      class={`flex items-center p-4  rounded-lg  text-red-400 ${
        theme === "night"
          ? "bg-gray-800 text-red-400"
          : "bg-fantasy border-solid border-2 border-gray-200 text-red-800"
      }`}
      role="alert"
    >
      <i class="fa-solid fa-triangle-exclamation"></i>
      <span class="sr-only">Error</span>
      <div class="ms-3 text-sm font-medium">{error}</div>
      <button
        onClick={() => {
          const updatedErrors = [...errors];
          updatedErrors.splice(index, 1);
          setErrors(updatedErrors);
        }}
        type="button"
        class="ms-auto -mx-1.5 -my-1.5 bg-red-50 text-red-500 rounded-lg focus:ring-2 focus:ring-red-400 p-1.5 hover:bg-red-200 inline-flex items-center justify-center h-8 w-8 dark:bg-gray-800 dark:text-red-400 dark:hover:bg-gray-700"
      >
        <span class="sr-only">Close</span>
        <i class="fa-solid fa-x"></i>
      </button>
    </div>
  );
}

export default Alert;
