import React, { useState, useEffect } from "react";

export const AdminAccount = ({ setSelected, selectedRow }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [dateCreated, setDateCreated] = useState(""); // New state for date
  const [status, setStatus] = useState(""); // New state for status

  useEffect(() => {
    // Populate fields with selected row data
    if (selectedRow) {
      setUsername(selectedRow.username);
      setEmail(selectedRow.email);
      setSelectedImage(selectedRow.image || null); // Assuming image is part of selectedRow
      setDateCreated(selectedRow.date); // New field for date
      setStatus(selectedRow.status); // New field for status
    }
  }, [selectedRow]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result); // Update the state with the new image
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = () => {
    // Add save functionality here
    console.log("Save button clicked");
  };

  const handleDisable = () => {
    // Add disable functionality here
    console.log("Disable button clicked");
  };

  return (
    <div className="flex items-center justify-center h-screen">
      <div className="max-w-4xl mx-auto bg-white p-8 rounded-lg flex shadow-lg relative">
        <button
          onClick={() => setSelected("Admin")} // Back to DataTable
          className="absolute top-4 left-4 text-black hover:text-gray-600"
        >
          <i className="fa-solid fa-circle-chevron-left text-3xl"></i>
        </button>

        {/* Image Holder */}
        <div className="w-1/2 p-4">
          <h2 className="text-2xl font-bold mb-4">Admin Account Details</h2>
          <div className="mb-4">
            {selectedImage ? (
              <img src={selectedImage} alt="Selected" className="w-full h-auto rounded-lg mb-4" />
            ) : (
              <div className="border-2 border-gray-300 h-40 flex items-center justify-center mb-4 rounded-lg">
                <span className="text-gray-500">No Image Selected</span>
              </div>
            )}
          </div>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            className="mb-4"
          />
          <input
            type="text"
            value={username}
            placeholder="Username"
            className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            readOnly // Makes it non-editable
          />
          <input
            type="email"
            value={email}
            placeholder="Email"
            className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            readOnly // Makes it non-editable
          />
          <input
            type="text"
            value={dateCreated}
            placeholder="Date Created"
            className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            readOnly // Makes it non-editable
          />
          <input
            type="text"
            value={status}
            placeholder="Status"
            className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            readOnly // Makes it non-editable
          />
          <div className="flex justify-between mt-4">
            <button
              onClick={handleSave}
              className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition duration-300"
            >
              Save
            </button>
            <button
              onClick={handleDisable}
              className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition duration-300"
            >
              Disable
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
