import React from "react";
import { ThemeContext } from "../../context/Theme";
import logo from "../../../src/assets/logo1.png";
import { useContext, useState, useEffect } from "react";
import { AlertsContext } from "../../context/Alerts";
import { SuccessContext } from "../../context/Success";
import { UserContext } from "../../context/User";
import axios from "axios";

export const ItemList = ({ setSelected }) => {
  const [image, setImage] = useState(null);
  const [name, setName] = useState("");
  const [value, setValue] = useState("");
  const [details, setDetails] = useState("");
  const [itemType, setItemType] = useState("S");
  const [itemholder, setItemHolder] = useState("diego silang");
  const [theme] = useContext(ThemeContext);
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const [user, setUser] = useContext(UserContext);
  const [items, setItems] = useState([]); // Store fetched items
  const [loading, setLoading] = useState(false); // Handle loading state

  const authToken = localStorage.getItem("authToken");
  const refreshToken = localStorage.getItem("refreshToken");
  const serverUrl = process.env.REACT_APP_SERVER_URL;

  // Fetch items from the database when component mounts
  useEffect(() => {
    setLoading(true);
    axios
      .get(`${serverUrl}/items`, {
        headers: {
          "x-auth-token": authToken,
          "x-refresh-token": refreshToken,
        },
      })
      .then((response) => {
        setItems(response.data); // Assume response contains the list of items
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
        setErrors(["Failed to fetch items."]);
        setLoading(false);
      });
  }, [authToken, refreshToken, serverUrl, setErrors]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setImage(imageUrl);
    }
  };

  const handleAddItem = () => {
    setErrors([]);
    setSuccess(false);
    if (!image) {
      setSuccess(false);
      setErrors(["No file selected. Please select an image file"]);
      return;
    }

    // Check file type (case-insensitive)
    const allowedTypes = ["image/png", "image/jpeg", "image/jpg"];
    if (!allowedTypes.includes(image.type.toLowerCase())) {
      setSuccess(false);
      setErrors(["Invalid file type. Only PNG, JPG, and JPEG are allowed"]);
      return;
    }

    // Check file size (less than or equal to 5 MB)
    const maxSize = 5 * 1024 * 1024; // 5 MB in bytes
    if (image.size > maxSize) {
      setSuccess(false);
      setErrors(["File size exceeds 5 MB. Please choose a smaller file."]);
      return;
    }

    const formData = new FormData();
    formData.append("image", image);
    formData.append("name", name);
    formData.append("value", value);
    formData.append("itemType", itemType);
    formData.append("details", details);
    formData.append("itemholder", itemholder);

    axios
      .post(`${serverUrl}/item/add`, formData, {
        headers: {
          "x-auth-token": authToken,
          "x-refresh-token": refreshToken,
          "Content-Type": "multipart/form-data", // Important for file uploads
        },
      })
      .then((response) => {
        setSuccess(true);
        setErrors(["Success! Item has been added"]);
        setUser(response.data.account);
        refetchItems(); // Refetch items after adding a new one
      })
      .catch((error) => {
        console.log(error);
        setSuccess(false);
        setErrors(error.response.data.errors.map((error) => error.msg));
      });
  };

  // Refetch items function
  const refetchItems = () => {
    setLoading(true);
    axios
      .get(`${serverUrl}/items`, {
        headers: {
          "x-auth-token": authToken,
          "x-refresh-token": refreshToken,
        },
      })
      .then((response) => {
        setItems(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
        setErrors(["Failed to fetch items."]);
        setLoading(false);
      });
  }
};

const ItemCard = ({ setSelected, user }) => {
  const [theme, setTheme] = useContext(ThemeContext);
  return (
    <div
      className={`relative max-w-xs mx-auto shadow-lg rounded-lg overflow-hidden m-4 min-w-55 ${
        theme === "night" ? "bg-night text-white" : "bg-fantasy text-black"
      }`}
    >
      <div className="absolute top-2 right-2">
        <button
          id="btn_update"
          onClick={() => {
            setSelected("UpdateItem");
          }}
        >
          <i className="fas fa-edit"></i>
        </button>
      </div>

      <img className="w-full h-auto" src={logo} alt="card" />
      <div className="p-4">
        <h1 className="font-bold text-xl mb-2">Skin Name</h1>
        <p className="text-base">Skin Description</p>
        <div className="mt-4 flex flex-col sm:flex-row">
          {user.role === "P" ? (
            <button className="bg-green-500 px-4 py-2 rounded mr-2 mb-2 sm:mb-0 text-white">
              Buy
            </button>
          ) : (
            <button
              id="btn_item_details"
              onClick={() => {
                setSelected("ItemDetails");
              }}
              className="bg-blue-500 px-4 py-2 rounded text-white"
            >
              See Details
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ItemCard;
