import React, { useState, useContext } from "react";
import { ThemeContext } from "../../context/Theme";
/*import useAxios from "../../hooks/useAxios";*/

export const AddNotification = ({ setSelected }) => {
  const [theme, setTheme] = useContext(ThemeContext);
  const [isOpen, setIsOpen] = useState(false);

  //Make the dropdown menu funcitonal or visible
  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div
      className={`col-span-8 overflow-hidden rounded-lg text-xs md:text-md w-64 px-8 sm:w-full py-10 ${
        theme === "night" ? "bg-night text-white " : "bg-fantasy text-black"
      }`}
    >
      <div className="flex justify-between w-full">
        <h1 className="text-2xl font-bold">Add Notification</h1>
        <button
          id="btn_back_AddNotification"
          onClick={() => {
            setSelected("Notification");
          }}
          className="rounded-lg px-4"
        >
          <i class="fa-solid fa-circle-chevron-left text-3xl"></i>
        </button>
      </div>

      <div className="flex flex-col  mt-8">
        <h1 className="text-2xl font-bold">Title</h1>
        <input
          type="text"
          className="w-full border border-gray-300 rounded-lg px-2 py-1 mt-2"
          placeholder="Enter title..."
          //value={title}
          // onChange={(e) => setTitle(e.target.value)}
        />

        <h1 className="text-2xl font-bold mt-4">Message</h1>
        <textarea
          className="w-full h-24 border border-gray-300 rounded-lg px-2 py-1 mt-2"
          placeholder="Enter message..."
          //value={message}
          // onChange={(e) => setMessage(e.target.value)}
        ></textarea>

        <h1 className="text-2xl font-bold mt-4">Type</h1>

        {/* Dropdown Menu */}
        <div className="relative inline-block text-left mt-4">
          <div>
            <button
              type="button"
              className="inline-flex justify-start w-100 rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-sm font-medium text-gray-700 hover:bg-gray-50"
              id="options-menu"
              aria-haspopup="true"
              aria-expanded={isOpen ? "true" : "false"}
              onClick={toggleDropdown}
            >
              Type of Message
              <svg
                className="-mr-1 ml-2 h-5 w-5"
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 20 20"
                fill="currentColor"
                aria-hidden="true"
              >
                <path
                  fillRule="evenodd"
                  d="M10 12a1 1 0 01-.7-.3l-4-4a1 1 0 111.4-1.4L10 10.6l3.3-3.3a1 1 0 111.4 1.4l-4 4a1 1 0 01-.7.3z"
                  clipRule="evenodd"
                />
              </svg>
            </button>
          </div>

          {/* Dropdown options */}
          {isOpen && (
            <div
              className="origin-top-left absolute left-0 mt-2 w-56 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 focus:outline-none"
              role="menu"
              aria-orientation="vertical"
              aria-labelledby="options-menu"
            >
              <div className="py-1" role="none">
                <button
                  className="w-full block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900"
                  role="menuitem"
                >
                  Global
                </button>
                <button
                  className="w-full block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900"
                  role="menuitem"
                >
                  Player
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Add Notification Button */}
        <div className="flex justify-end">
          <button
            id="btn_createnotification"
            onClick={() => {
              setSelected("Notification");
            }}
            className="bg-green-500 text-white rounded-lg px-4 py-2 mt-4 max-w-40"
          >
            Create Notification
          </button>
        </div>
      </div>
    </div>
  );
};
