import React, { useState, useContext } from "react";
import { ThemeContext } from "../../context/Theme";

const DataTable = ({ accvalues, setSelected }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 5;
  const [theme] = useContext(ThemeContext);

  // Determine styles based on theme
  const textColor = theme === "night" ? "text-white" : "text-black";
  const bgColor = theme === "night" ? "bg-night" : "bg-fantasy";
  const headerColor = theme === "night" ? "bg-gray-800" : "bg-gray-200";

  // Filter accvalues based on the search term
  const filteredData = accvalues.filter((row) => {
    const { username, email, date, status } = row;
    const dateMonthYear = new Date(date).toLocaleString("default", {
      month: "long",
      year: "numeric",
    });

    return (
      username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      dateMonthYear.toLowerCase().includes(searchTerm.toLowerCase()) ||
      status.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  const totalPages = Math.ceil(filteredData.length / rowsPerPage);

  return (
    <div
      className={`flex col-span-8 overflow-hidden rounded-lg shadow-lg  text-xs md:text-md w-64 px-8 sm:w-full py-10 mb-5 ${
        theme === "night" ? "bg-night  text-white " : "bg-fantasy text-black"
      }`}
    >
      <div className="p-6 w-full">
        <div className="flex justify-center ">
          <table className="table-auto w-full mt-8 text-center ">
            <thead>
              <tr>
                <th>Action</th>
                <th>Username</th>
                <th>Email</th>
                <th>Date Created</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {Sampledata.map((item) => (
                <tr key={item.id}>
                  <td className="px-4 py-2 justify-center">
                    <button
                      id="btn_Notification_action"
                      onClick={() => {
                        setSelected("Receipt");
                      }}
                      className="hover:text-blue-700  font-bold py-2 px-4 rounded"
                    >
                      <i class="fa-solid fa-eye"></i>
                    </button>
                  </td>
                  <td className="px-4 py-2">{item.name}</td>
                  <td className="px-4 py-2">{item.type}</td>
                  <td className="px-4 py-2">{item.mode}</td>
                  <td className="px-4 py-2">{item.dateupdate}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        {/* Pagination */}
        <div className="flex justify-end mt-5">
          <div className="flex items-center">
            <button className="py-2 px-4 mr-2">&lt;</button>
            <button className="py-2 px-4">1</button>
            <button className="py-2 px-4 ml-2">&gt;</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DataTable;
