import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Register necessary components for Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend
);

const SalesAndRevenueChart = ({ theme }) => {
  const [data, setData] = useState({
    labels: [
      "Jan",
      "Feb",
      "Mar",
      "Apr",
      "May",
      "Jun",
      "Jul",
      "Aug",
      "Sep",
      "Oct",
      "Nov",
      "Dec",
    ],
    datasets: [
      {
        label: "Sales",
        data: [120, 150, 200, 180, 250, 300, 350, 400, 450, 500, 550, 600],
        borderColor: theme === "night" ? "#E5E7EB" : "#1F2937", // Line color for Sales dataset
        backgroundColor: "rgba(229, 231, 235, 0.2)", // Light fill for Sales
        fill: true,
      },
      {
        label: "Revenue",
        data: [
          1000, 1200, 1500, 1600, 1800, 2000, 2200, 2400, 2600, 2800, 3000,
          3200,
        ],
        borderColor: theme === "night" ? "#E5E7EB" : "#1F2937", // Line color for Revenue dataset
        backgroundColor: "rgba(229, 231, 235, 0.2)", // Light fill for Revenue
        fill: true,
      },
    ],
  });

  const [options, setOptions] = useState({
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "Sales and Revenue Chart",
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: "Month",
          color: theme === "night" ? "#E5E7EB" : "#1F2937", // Adjust axis label color
        },
      },
      y: {
        title: {
          display: true,
          text: "Amount",
          color: theme === "night" ? "#E5E7EB" : "#1F2937", // Adjust axis label color
        },
      },
    },
  });

  // You can add logic here to update the chart data dynamically
  useEffect(() => {
    // Any dynamic data fetching or updates can go here
  }, []);

  // Define colors based on the theme
  const themeColors =
    theme === "night"
      ? {
          topBorderColor: "#1F2937", // Dark border for top
          bgColor: "#1F2937", // Dark background color
          textColor: "#E5E7EB", // Light text color for dark theme
        }
      : {
          topBorderColor: "#E5E7EB", // Light blue border for top
          bgColor: "#FFFFFF", // White background for light theme
          textColor: "#1F2937", // Dark text color for light theme
        };

  return (
    <div
      className="chart-container"
      style={{
        padding: "20px",
        backgroundColor: themeColors.bgColor,
        color: themeColors.textColor,
        borderTop: `5px solid ${themeColors.topBorderColor}`,
        borderRadius: "10px",
        maxWidth: "900px",
        margin: "auto",
      }}
    >
      <h2>Sales and Revenue Chart</h2>
      <Line data={data} options={options} />
    </div>
  );
};

export default SalesAndRevenueChart;
