import React, { useState, useEffect, useContext } from "react";
import { SuccessContext } from "../../context/Success";
import { AlertsContext } from "../../context/Alerts";
import { ThemeContext } from "../../context/Theme";
import logo from "../../assets/logo1.png";
import API from "../../service/API";

export const PlayerAccount = ({ setSelected, selectedplayer }) => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [dateCreated, setDateCreated] = useState("");
  const [status, setStatus] = useState(selectedadmin.acc_enabled);
  const [wins, setWins] = useState("");
  const [loses, setLoses] = useState("");
  const [gold, setGold] = useState("");
  const [perlas, setPerlas] = useState("");
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const serverUrl = process.env.REACT_APP_SERVER_URL;
  const [useraccount, setUserAccount] = useState({});
  const [theme, setTheme] = useContext(ThemeContext);

  useEffect(() => {
    const selectedPlayer = playerValues.find((player) => player.id === 1);
    if (selectedPlayer) {
      setUsername(selectedPlayer.username);
      setEmail(selectedPlayer.email);
      setDateCreated(selectedPlayer.date);
      setStatus(selectedPlayer.status === "Online"); // Convert string to boolean
    }
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = () => {
    console.log("Player data saved successfully");
  };

  const handleToggleStatus = () => {
    setStatus((prevStatus) => {
      const newStatus = !prevStatus;
      console.log(newStatus ? "Player is now Online" : "Player is now Offline");
      return newStatus;
    });
  };

  return (
    <div className="flex items-center justify-center h-screen">
      <div className="max-w-4xl mx-auto bg-white p-8 rounded-lg flex shadow-lg relative flex-col md:flex-row">
        {/* Back Button */}
        <button
          onClick={() => setSelected("Player")}
          className="absolute top-4 left-4 text-black hover:text-gray-600"
        >
          <i className="fa-solid fa-circle-chevron-left text-3xl"></i>
        </button>

        {/* Image Holder */}
        <div className="w-full md:w-1/2 p-4 flex flex-col items-center">
          <div className="mb-4">
            {selectedImage ? (
              <img src={selectedImage} alt="Selected" className="w-full h-auto rounded-lg mb-4" />
            ) : (
              <img
                src={defaultPlayerImage}
                alt="Default"
                className="w-full h-auto rounded-lg mb-4"
              />
            )}
          </div>
          <input type="file" accept="image/*" onChange={handleImageChange} className="mb-4" />
        </div>

        {/* Player Details */}
        <div className="w-full md:w-1/2 p-4">
          <h2 className="text-2xl font-bold mb-4">Player Account Details</h2>

          <div className="mb-4">
            <label className="block mb-1" htmlFor="username">
              Username
            </label>
            <input
              id="username"
              type="text"
              value={username}
              placeholder="Username"
              onChange={(e) => setUsername(e.target.value)}
              className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            />
          </div>

          <div className="mb-4">
            <label className="block  mb-1" htmlFor="email">
              Email
            </label>
            <input
              id="email"
              type="email"
              value={email}
              placeholder="Email"
              onChange={(e) => setEmail(e.target.value)}
              className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            />
          </div>

          <div className="mb-4">
            <label className="block  mb-1" htmlFor="dateCreated">
              Date Created
            </label>
            <input
              id="dateCreated"
              type="datetime-local"
              value={dateCreated}
              onChange={(e) => setDateCreated(e.target.value)}
              className="w-full mb-4 p-2 rounded-lg border border-gray-300"
            />
          </div>

          {/* Toggle Status Button */}
          <div className="flex justify-end items-center mb-4">
            <button
              onClick={handleToggleStatus}
              className={`py-2 px-4 rounded text-white ${
                status ? "bg-red-500 hover:bg-red-700" : "bg-green-500 hover:bg-green-700"
              }`}
            >
              {status ? "Set Offline" : "Set Online"}
            </button>
          </div>

          {/* Save Button */}
          <div className="flex justify-end mt-4">
            <button
              onClick={handleSave}
              className="bg-green-500 hover:bg-green-700 text-white py-2 px-4 rounded"
            >
              Save
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PlayerAccount;
