import React, { useState, useEffect, useContext } from "react";
import shane from "/Github/Pugad Testing/Pugad-Maharlika-Frontend/client/src/assets/James-Reid.jpg";
import { SuccessContext } from "../../context/Success";
import { AlertsContext } from "../../context/Alerts";
import API from "../../service/API";

export const AdminAccount = ({ setSelected, selectedadmin }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [dateCreated, setDateCreated] = useState("");
  const [status, setStatus] = useState(true);
  const [wins, setWins] = useState("");
  const [loses, setLoses] = useState("");
  const [gold, setGold] = useState("");
  const [perlas, setPerlas] = useState("");
  const [success, setSuccess] = useContext(SuccessContext);
  const [errors, setErrors] = useContext(AlertsContext);
  const serverUrl = process.env.REACT_APP_SERVER_URL;
  const [useraccount, setUserAccount] = useState({});

  useEffect(() => {
    const handleViewAdmins = async () => {
      setErrors([]);
      setSuccess(false);
      const config = {
        url: `${serverUrl}/account/view`,
        method: "POST",
        data: { id: selectedadmin },
      };

      const { res, error } = await API(config);
      if (res) {
        setUserAccount(res.data.useraccount);
      }
      if (error) {
        setErrors(error.response.data.errors.map((error) => error.msg));
      }
    };
    handleViewAdmins();
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleToggleStatus = () => {
    setStatus((prevStatus) => !prevStatus);
  };

  return (
    <div className="flex justify-center items-start mt-10">
      <div className="max-w-6xl w-full bg-white p-8 rounded-lg shadow-lg flex flex-col md:flex-row">
        {/* Left Column: Profile Image */}
        <div className="w-full md:w-1/3 p-4 flex flex-col items-center">
          <button
            onClick={() => setSelected("Admin")}
            className="absolute top-4 left-4 text-black hover:text-gray-600"
          >
            <i className="fa-solid fa-circle-chevron-left text-3xl"></i>
          </button>

          {/* Image Holder */}
          <div className="mb-4">
            {useraccount.acc_profile ? (
              <img
                src={useraccount.acc_profile}
                alt="Selected"
                className="w-full h-auto rounded-lg mb-4"
              />
            ) : (
              <img
                src={shane}
                alt="Default"
                className="w-full h-auto rounded-lg mb-4"
              />
            )}
          </div>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            className="mb-4"
          />
        </div>

        {/* Middle Column: Username and Email */}
        <div className="w-full md:w-1/3 p-4">
          <h2 className="text-2xl font-bold mb-4">Account Info</h2>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1">Username</label>
            <input
              type="text"
              value={useraccount.acc_username}
              placeholder="Username"
              className="w-full p-2 rounded-lg border"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1">Email</label>
            <input
              type="email"
              value={useraccount.acc_email}
              placeholder="Email"
              className="w-full p-2 rounded-lg border"
            />
          </div>
        </div>

        {/* Right Column: Stats */}
        <div className="w-full md:w-1/3 p-4">
          <h2 className="text-2xl font-bold mb-4">Account Stats</h2>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1">Wins</label>
            <input
              type="text"
              value={wins}
              placeholder="Wins"
              className="w-full p-2 rounded-lg border"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1">Loses</label>
            <input
              type="text"
              value={loses}
              placeholder="Loses"
              className="w-full p-2 rounded-lg border"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1">Win Rate</label>
            <input
              type="text"
              value={(wins / (wins + loses)) * 100 || 0}
              placeholder="Win Rate"
              className="w-full p-2 rounded-lg border"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1">Gold</label>
            <input
              type="text"
              value={gold}
              placeholder="Gold"
              className="w-full p-2 rounded-lg border"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1">Perlas</label>
            <input
              type="text"
              value={perlas}
              placeholder="Perlas"
              className="w-full p-2 rounded-lg border"
            />
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 mb-1">Date Created</label>
            <input
              disabled
              type="datetime-local"
              value={dateCreated}
              className="w-full p-2 rounded-lg border"
            />
          </div>
        </div>
      </div>
    </div>
  );
};
