import React, { useContext } from "react";
import Theme, { ThemeContext } from "../../context/Theme";

export const ViewOffer = ({ setSelected, user }) => {
  const [theme, setTheme] = useContext(ThemeContext);
  return (
    <div
      className={`col-span-8 overflow-hidden rounded-lg shadow-lg  text-xs md:text-md w-64 px-8 sm:w-full py-10 ${
        theme === "night" ? "bg-night text-white " : "bg-fantasy text-black"
      }`}
    >
      <div className="flex justify-between w-full px-4">
        <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          Back
        </button>
        <button className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded">
          Edit
        </button>
      </div>
      <div className="flex items-center mt-5">
        <img
          src="https://source.unsplash.com/random"
          alt="Item"
          className="w-1/2"
        />
        <div className="ml-5">
          <h2 className="text-2xl font-bold">Item Title</h2>
          <p className="text-gray-600 mt-2">Price: $100</p>
          <p className="text-gray-600">
            Description: Lorem ipsum dolor sit amet, consectetur adipiscing
            elit.
          </p>
          <p className="text-gray-600">Date Created: 01/01/2022</p>
          <p className="text-gray-600">Date Updated: 01/02/2022</p>
        </div>
      </div>
    </div>
  );
};
