import React from "react";
import { ThemeContext } from "../../context/Theme";
import logo from "../../../src/assets/logo1.png";

const ItemCard = () => {
  return (
    
    <div className="relative max-w-xs mx-auto bg-white shadow-lg rounded-lg overflow-hidden m-4 min-w-55 ml-4">
  {/* Edit icon using FontAwesome */}
  <div className="absolute top-2 right-2">
    <button>
      <i className="fas fa-edit text-gray-600 hover:text-gray-800"></i>
    </button>
  </div>

  <img className="w-full" src={logo} alt="card" />
  <div className="p-4">
    <h1 className="text-gray-900 font-bold text-xl mb-2">Skin Name</h1>
    <p className="text-gray-600 text-base">Skin Description</p>
    <div className="mt-4">
      <button className="bg-blue-500 text-white px-4 py-2 rounded mr-2">Buy</button>
      <button className="bg-green-500 text-white px-4 py-2 rounded">See Details</button>
    </div>
  </div>
</div>
    );
};

export default ItemCard;
