import { React, useEffect, useState } from "react";

function NavBar({ theme, toggleTheme, toggleSideBar }) {
  const [scrolled, setScrolled] = useState(false);
  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      if (offset > 0) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="h-auto  w-full flex-2 flex flex-row z-50 py-3 bg-base-100 border-b-2  border-gray-200">
      <div className="flex-1 navbar-start">
        <button onClick={toggleSideBar} className="ml-2 btn bg-transparent   ">
          <i className="fa-solid fa-bars"></i>
        </button>
      </div>
      <div className="navbar-center flex-1 hidden lg:flex"></div>
      <div className="flex-1 flex gap-3 justify-end mr-2">
        <button onClick={toggleTheme} className="btn btn-square btn-outline">
          {theme === "night" ? (
            <i className="fa-regular fa-moon"></i>
          ) : (
            <i className="fa-regular fa-sun"></i>
          )}
        </button>
      </div>
    </div>
  );
}

export default NavBar;
