import React, { useState, useContext, useEffect } from "react";
import { ThemeContext } from "../../context/Theme";

export const AdminAccount = ({ setSelected, selectedRow }) => {
  const [theme, setTheme] = useContext(ThemeContext);
  const [selectedImage, setSelectedImage] = useState(null);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  useEffect(() => {
    // Populate fields with selected row data
    if (selectedRow) {
      setUsername(selectedRow.username);
      setEmail(selectedRow.email);
      setSelectedImage(selectedRow.image); // Assuming the row contains an image property
      // Reset password fields for editing
      setPassword("");
      setConfirmPassword("");
    }
  }, [selectedRow]);

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const img = URL.createObjectURL(e.target.files[0]);
      setSelectedImage(img);
    }
  };

  const handleEditAccount = () => {
    // Logic to handle account editing goes here
    console.log("Edited Account:", { username, email, password, selectedImage });
  };

  return (
    <div className="flex items-center justify-center h-screen">
      <div className="max-w-4xl mx-auto bg-white p-8 rounded-lg flex shadow-lg relative">
        <button 
          onClick={() => { setSelected("Admin") }} 
          className="absolute top-4 left-4 text-black hover:text-gray-600"
        >
          <i className="fa-solid fa-circle-chevron-left text-3xl"></i>
        </button>

        {/* Image Holder */}
        <div className="w-1/2 p-4">
          <h2 className="text-2xl font-bold mb-4">Admin Account</h2>
          <div className="mb-4">
            {selectedImage ? (
              <img src={selectedImage} alt="Selected" className="w-full h-auto rounded-lg mb-4" />
            ) : (
              <div className="border-2 border-gray-300 h-40 flex items-center justify-center mb-4 rounded-lg">
                <span className="text-gray-500">No Image Selected</span>
              </div>
            )}
            <input 
              type="file" 
              accept="image/*" 
              onChange={handleImageChange} 
              className="hidden" 
              id="select-image" 
            />
            <label 
              htmlFor="select-image" 
              className="inline-block bg-green-500 text-white px-4 py-2 rounded-lg cursor-pointer hover:bg-green-600"
            >
              Select Image
            </label>
          </div>
          <input 
            type="text" 
            value={username} 
            onChange={(e) => setUsername(e.target.value)} 
            placeholder="Username" 
            className="w-full mb-4 p-2 rounded-lg border border-gray-300" 
          />
          <input 
            type="email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            placeholder="Email" 
            className="w-full mb-4 p-2 rounded-lg border border-gray-300" 
          />
          <input 
            type="password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            placeholder="Password" 
            className="w-full mb-4 p-2 rounded-lg border border-gray-300" 
          />
          <input 
            type="password" 
            value={confirmPassword} 
            onChange={(e) => setConfirmPassword(e.target.value)} 
            placeholder="Confirm Password" 
            className="w-full mb-4 p-2 rounded-lg border border-gray-300" 
          />
          <div className="flex justify-between">
            <button 
              onClick={handleEditAccount} // Call edit handler
              className="rounded-lg bg-green-500 text-white px-4 py-2 hover:bg-green-600"
            >Edit Account
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
