import React from "react";

const ConfirmationDialog = ({ theme, handleAction, id, message, buttonText }) => {
  return (
    <>
      <dialog
        id={id}
        className="modal backdrop-brightness-50 z-30 flex flex-grow w-screen place-self-center fixed  h-screen place-items-center "
      >
        <div
          className={`w-80 max-w-sm justify-center sm:w-full place-self-center px-4 pt-1 pb-4 mx-auto border border-gray-200 rounded-lg shadow sm:p-6 md:p-8 md:pt-4 ${
            theme === "night" ? "bg-night text-white" : "bg-fantasy text-night"
          }`}
        >
          <div className="flex w-full  ">
            <span className="flex-2 w-full "></span>

            <button
              onClick={() => document.getElementById(id).close()}
              className="flex-1 hover:underline mt-2  "
            >
              <i className="fa-solid fa-x "></i>
            </button>
          </div>
          <h5 className="text-md w-full text-center space-y-4 font-medium mb-5 ">{message}</h5>

          <button
            onClick={handleAction}
            type="button"
            className="w-full text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
          >
            {buttonText}
          </button>
          <button
            onClick={handleAction}
            type="button"
            className="w-full text-white bg-grey-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center "
          >
            Cancel
          </button>
        </div>
      </dialog>
    </>
  );
};

export default ConfirmationDialog;
